INSERT INTO `sys_city` VALUES ('140900', '240', '140000', '忻州', 'XZ', 'X', '112.734198', '38.416182', 'https://imgb.mofangx.com/test/2933bdcf124d4eac949134dae7245b2f.jpg', '殊像寺,塔院寺,显通寺,', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">忻州市位于山西省中北部。北邻大同、朔州，南毗太原，西隔黄河与陕西、内蒙相望，东以太行山与河北接壤，险关要塞，兵家必争，故有“晋北锁钥”之称。忻州旅游资源独具特色。山、水、庙、关、林、泉、洞等名胜古迹遍布，著名的有四大佛教胜地之首—五台山、芦芽山、赵杲观、天涯山等国家级森林公园；万里长城第一古塞雁门关、“华北第一溶洞”禹王洞、现存最大的祠宇杨家祠堂、旧中国最大的封建官僚私邸闫锡山故居。还有悬空村、宁武天池、万年冰洞等一大批精华旅游景点。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">秋季</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 2