INSERT INTO `sys_city` VALUES ('141100', '240', '140000', '吕梁', 'LL', 'L', '111.14454', '37.518996', '', '', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">吕梁地区是中华名族发源地之一，属于典型的黄土高原地貌。吕梁因吕梁山脉由北向南纵申全境而得名。吕梁为典型的黄土高原地貌，境内沟壑纵横，山峦起伏，梯田环绕。这里人杰地灵、物华天宝，有着丰富的矿产资源和人文资源。是三晋大地上一颗璀璨的明星。吕梁市旅游资源数量大，个性突出。境内自然景观旅游资源优美，文物古迹景点甚多，革命纪念地和革命遗址闻名中外，享有“三晋第一名山”的“北武当山”，以其雄伟壮观、山势陡峭、奇岩怪石、刑态神妙而著称。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">5-10月最佳。春季日温差大，风沙多。冬寒夏暖，秋季气候温和。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">3天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">半干旱大陆性季风气候，四季分明，差异悬殊。春季干燥，雨少风多；夏季炎热，雨量集中；秋季凉爽，气候宜人；冬季寒冷，降雪偏少。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-20 15:42:19', '', '0', 'group1/M01/40/C1/eBif6Fj4Y8-AUAg3AAHUcs8ldqI690.jpg', 'group1/M01/40/C1/eBif6Fj4ZjyAF8oeAAUPJp0fUSw601.jpg,group1/M01/40/C1/eBif6Fj4Zj-AcRXWAAKcm2HQyvQ655.jpg,group1/M01/40/C1/eBif6Fj4ZkGAX1XjAABjXmkA4bA018.jpg,group1/M01/40/C1/eBif6Fj4ZkSAR8vfAALCR6W3cC4439.jpg,group1/M01/40/C1/eBif6Fj4ZkeAeu4PAAEMclkCiMA114.jpg');
INSERT INTO `sys_city` VALUES ('150100', '240', '150000', '呼和浩特', 'HHHT', 'H', '111.748426', '40.842723', 'https://imgb.mofangx.com/test/b9c308dc99ad47418500dbbad78a0f7d.jpg', '大召寺,内蒙古博物馆,昭君博物院,', '1', '0', '1', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">呼和浩特是一座有400多年建城历史、具鲜明民族特点和众多名胜古迹的塞外名城。这里有各种召庙50多座，城郊有不少风光秀丽的草原旅游点，牛羊遍地，鸟语花香，大黑河南岸有一座传诵古今的西汉古墓——昭君墓，青冢兀立、巍峨壮观；有“塞外西湖”之称的哈素海，是天然湖泊，晴空碧水，蔚蓝一色；与蒙古风情街相连的伊斯兰风情街，以沙漠黄为主，绿白相间为副色，圆形殿顶、高耸的柱式塔楼，让人领略到浓郁的伊斯兰风情。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆可，夏秋最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">1-2天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">中温带大陆性季风气候</p></div><div id=\"intro_unstrt-travellanguage\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">晋语-张呼片，蒙语</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 11:54:46', '', '0', 'group1/M01/41/40/eBif6Fj5gk-Aa1v6AAMS35FJDWo235.jpg', 'group1/M01/41/40/eBif6Fj5gmaAd-0PAADmcyyxWNA898.jpg,group1/M01/41/40/eBif6Fj5gmmALwanAALtKOkbmSo458.jpg,group1/M01/41/40/eBif6Fj5gm-AaBSFAAfGFe53Vfk979.jpg,group1/M01/41/40/eBif6Fj5gnGAR5rxAAPq4Lzqwww313.jpg,group1/M01/41/40/eBif6Fj5gnSAOk57AAS--vZ0hVw672.jpg,group1/M01/41/40/eBif6Fj5gnmAa1fQAACYMkavLqA060.jpg');
INSERT INTO `sys_city` VALUES ('150200', '240', '150000', '包头', 'BT', 'B', '109.953648', '40.621863', 'https://imgb.mofangx.com/test/736234773bd2442aaa3c1bc30549c0b8.jpg', '五当召,美岱召,北方兵器城,', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_25110\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">包头的名字，源于蒙古语“包克图”，意思是“有鹿的地方”，所以包头也被称作是“草原鹿城”。它是内蒙古最大的城市，也是经济最发达的内蒙城市之一。他位于蒙古高原的南端，南濒黄河，阴山山脉横贯该市中部，独特的地理位置使得包头成为游牧民族和农耕民族交往的前沿地带，旅蒙晋商互市之地。想当年，无数山西人千里迢迢走过西口，来到这里，也带来了中原的农耕文化，从此西北文化就与游牧文化在这里交融繁衍。如今的包头，无论从饮食文化上，还是生活习惯上，都能感受到独特的西北风情。当你坐在温暖的蒙古包中，吃上一口沾满羊汤的莜面，才能真正体会到包头历史与文化的精髓所在。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_25111\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">包头属半干旱中温带大陆性季风气候，年均气温8.5℃，年最低气温－27.6℃，年最高气温35.5℃。夏秋两季（6月中下旬至9月初），绝对是来包头旅游的最佳时间。此时的包头清风送爽，花香色艳，瓜果丰盛，蔬菜充足，是理想的避暑胜地。春季不仅寒冷而且多发沙尘暴，不适合出游；而冬季气温很低，届时包头会冰封雪飘，银装素裹，别具北国特色，如果不畏惧内蒙的严寒，也可以来这里欣赏纯洁的冰雪世界。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_25116\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">由于包头市多民族文化交融之地，所以当地有几种不同的常用语言。<br>普通话<br>也就是全国通用的普通话，与官方语言一致，在包头绝大部分城区都通行。<br>包头话<br>特指流行于包头市东河区、九原区等区县的晋语包头方言，属于晋语大同包头片。由于受蒙古语的影响，晋语包头方言同时融入一些蒙古语的音译词汇。<br>东北话<br>主要流行于昆都仑区，建国后援建包钢时，很多东北人从工业基础不错的东北移民来包，主要来源于辽宁，东北方言主要通行于这些东北人当中。<br>蒙古语<br>官方承认的正式语言之一，在全市机关单位和商店的招牌上均有使用，但民间使用人数很少。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_25117\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">如果是6月中下旬至9月初来到包头，这里正值夏秋两季，白天气温比较高，可以穿夏季的短袖和裙子，但是一定要记得带一件长袖衫和长裤，包头的早晚温差会比较大，夜间比较寒冷，而且去草原游玩的时候，穿长裤也比较方便。夏季前来一定要携带防晒霜、墨镜、遮阳帽或者遮阳伞等物品，这里紫外线强烈，要做好防晒措施。包头气候比较干燥，无论何时前来，都记得携带一些保湿保养品。如果冬季前来，保暖工作要做好，羽绒服、厚毛衣、长围巾、毛线帽等都是必要的衣物；如果可能，还可以带一件防雨雪的冲锋衣，这样碰到大风雪天气，也不会打湿衣物让自己更加寒冷。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 10:52:10', '', '0', 'group1/M01/41/3F/eBif6Fj5ccOAeNUwAAJgDrBr0UE063.jpg', 'group1/M01/41/3F/eBif6Fj5c6iAY9ffAAH4JbCvMFQ542.jpg,group1/M01/41/3F/eBif6Fj5c6yAFbVWAAQiDulwliM283.jpg,group1/M01/41/3F/eBif6Fj5c7GAVHBxAADNcvyOMmY574.jpg,group1/M01/41/3F/eBif6Fj5c7WABdFFAAj9Hv9ViZY717.jpg,group1/M01/41/3F/eBif6Fj5c7uAD-yEAAxzWpUvTEU126.jpg');
INSERT INTO `sys_city` VALUES ('150300', '240', '150000', '乌海', 'WH', 'W', '106.79419', '39.655768', '', '', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">乌海市是内蒙古自治区西部的新兴工业城市，地处黄河上游，华北与西北的结合部，同时也是“宁蒙陕甘”经济区的结合部和沿黄经济带的中心区域。乌海市具有丰富的旅游资源，境内群山环绕，戈壁、荒漠、沙漠、草原分布期间。黄河穿境而过逾百公里，由南向北顺流而下，沿途分布着大大小小十几个河心岛；沙丘连绵，酷似海湾的金沙湾；一线天凌空盘踞，给人一种神秘感；古城旅游有汉代古城遗址、明代烽火台、马堡店遗址、秦长城遗址等。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，秋季最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">3天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">温带大陆性气候</p></div><div id=\"intro_unstrt-travellanguage\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">兰银官话-银吴片、晋语-五台片</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 14:29:58', '', '0', 'group1/M01/41/40/eBif6Fj5pAuAbWd0AAFpfkPAVeU600.jpg', 'group1/M01/41/41/eBif6Fj5prqAWV5tAAD75N3coHA087.jpg,group1/M01/41/41/eBif6Fj5pr2AYE8XAAGXhfuE5Qc920.jpg,group1/M01/41/41/eBif6Fj5psCAEhyOAABgloRYpQY936.jpg,group1/M01/41/41/eBif6Fj5psWALTH7AAEflrWEAso326.jpg,group1/M01/41/41/eBif6Fj5psiAfU6sAAIFn0WulGM648.jpg');
INSERT INTO `sys_city` VALUES ('150400', '240', '150000', '赤峰', 'CF', 'C', '118.88694', '42.257843', 'https://imgb.mofangx.com/test/70ff7ba86458486f8e136b9c763b9839.jpg', '达里诺尔湖,贡格尔草原,阿斯哈图石林,', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">赤峰是内蒙古草原上一座美丽的小城，这里有清新的市景、奇特的山峰、辽阔无际的草原、源远流长的历史、令人垂涎的美食。来到这里你一定不可以错过乌兰布统的欧式草原风光，更不能错过怪石嶙峋拥有奇特景观的红山，还可以跟随着候鸟的脚步到达里诺尔湖感受内蒙古人心中的海。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，夏季最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">2天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">中温带半干旱大陆性季风气候</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 11:07:08', '', '0', 'group1/M01/41/3F/eBif6Fj5dBmAMyL9AAKQRuaAptU411.jpg', 'group1/M01/41/3F/eBif6Fj5dzeAUl8UAACZD-cU1Ew661.jpg,group1/M01/41/3F/eBif6Fj5dzqAcuDTAADFTxFSIkM561.jpg,group1/M01/41/3F/eBif6Fj5dz6AFF92AAF9QDKsOeI606.jpg,group1/M01/41/3F/eBif6Fj5d0GAOXR8AAMj-XHoFVU131.jpg,group1/M01/41/3F/eBif6Fj5d0aAJGkbAAIkzJ0R504105.jpg');
INSERT INTO `sys_city` VALUES ('150500', '240', '150000', '通辽', 'TL', 'T', '122.243282', '43.6536', '', '', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">通辽市位于内蒙古自治区东部、松辽平原西端，属中国东北地区。通辽市的前身为哲里木盟，始建于清朝崇德元年。通辽市土质肥沃、水草丰美，野生动物繁多，有国家一级保护动物丹顶鹤、白鹳、鸨、梅花鹿、紫貂，同时也是国家重要的畜牧业生产基地，被誉为\"黄牛之乡\"。通辽比较知名的景点有大青沟、珠日河草原、阿古拉草原、奈曼王府、扎鲁特草原、特金罕山等。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，夏季最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-hei