INSERT INTO `sys_city` VALUES ('150600', '240', '150000', '鄂尔多斯', 'EEDS', 'E', '109.782461', '39.608763', 'https://imgb.mofangx.com/test/440c0395caaa4ab9ba5b17718f672713.jpg', '响沙湾,成吉思汗陵旅游景区,鄂尔多斯大草原,', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">“鄂尔多斯”是蒙古语，意为“很多宫殿”。鄂尔多斯草原上，牧人们在奔驰的马背上放歌，姑娘们翩翩起舞；走进大漠，迎接黄沙的洗礼，沐浴恩格贝沙湖的凉爽，再乘骆驼去追寻蒙古古朴民风的足迹；畅游准格尔黄河大峡谷，赏两岸的悬崖绝壁与历史留痕；最后拜谒成吉思汉陵、游览“河套人”故地、十二连城、准格尔召，去领略鄂尔多斯的沧桑。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，夏秋最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">2天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">温带大陆性气候</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 11:30:46', '', '0', 'group1/M01/41/3F/eBif6Fj5fKyAN5J0AAIZRUFO6iY679.jpg', 'group1/M01/41/3F/eBif6Fj5fMOAZxrsAAbDbRIqKws964.jpg,group1/M01/41/3F/eBif6Fj5fMaAF2sNAAFmoOjUjC4970.jpg,group1/M01/41/3F/eBif6Fj5fMiAGt6AAACold1kKi4569.jpg,group1/M01/41/3F/eBif6Fj5fMyAcV3QAAKP6iMxdL8378.jpg,group1/M01/41/3F/eBif6Fj5fNKAMU7lAALQ6GROvm8316.jpg');
INSERT INTO `sys_city` VALUES ('150700', '240', '150000', '呼伦贝尔', 'HLBE', 'H', '119.779196', '49.166026', 'https://imgb.mofangx.com/test/5d623169f0a4473d9f16cb1443d5a1d4.jpg', '呼伦湖,呼伦贝尔大草原,恩和俄罗斯民族乡,', '0', '0', '0', '4', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">呼伦贝尔速览</div><div class=\"figure\" style=\"margin: 20px 0px; padding: 0px; word-wrap: break-word; font-size: 12px; color: rgb(153, 153, 153); line-height: 20px;\"><a href=\"http://n4-q.mafengwo.net/s6/M00/5C/18/wKgB4lJqgqyAa32QAAglUkXIpVk13.jpeg\" target=\"_blank\" title=\"点击查看原图\" style=\"color: rgb(153, 153, 153); text-decoration: none; cursor: pointer;\"><img src=\"http://n4-q.mafengwo.net/s6/M00/5C/18/wKgB4lJqgqyAa32QAAglUkXIpVk13.jpeg\" style=\"border: 0px; vertical-align: middle; margin-bottom: 8px; max-width: 100%;\"></a><div style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">图：呼伦贝尔莫尔道嘎。By 一切旎好</div></div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">呼伦贝尔位于内蒙古的最北端，北部靠近中国最北的村镇漠河，西与俄罗斯、蒙古接壤。这里的绝大部分森林、草原、湖泊等自然生态环境未经人类工业文明的洗礼，具有原始且古老的自然风貌，以及中俄、中蒙边境异域风情。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">呼伦贝尔常常被我们作为“大草原”的代名词，其实这里不仅有丰富的草原旅游资源，还有面积非常广阔的兴安岭林区。额尔古纳和莫尔道嘎林区在秋季时的盛景非常值得专门前往，这里还有国内唯一放牧驯鹿的敖鲁古雅驯鹿园。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">南部大部分草原区已经有着完善的旅游开发，可以骑马、射箭、夜宿蒙古包，对电视剧中豪放不羁的蒙古族人生活小试身手。当然还有令人垂涎的草原美食，这里鲜美的牛羊肉可以让远道而来的游客大快朵颐。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><h5 style=\"margin: 0px; padding: 0px; font-size: 14px;\">呼伦贝尔北部林区</h5><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">最佳旅行季节为5月中下旬和9月中下旬，这两个时候气温适宜，没有蚊虫干扰。5月树木新绿，漫山遍野开满杜鹃花；9月，秋色染遍大兴安岭，是摄影的好时光。</p><h5 style=\"margin: 0px; padding: 0px; font-size: 14px;\">呼伦贝尔南部草原区</h5><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">最佳旅行季节为6-8月。11月至次年2月中旬则是冰雪游的最佳时节。</p><div class=\"tips\" style=\"margin: 20px 0px 0px; padding: 15px 20px; word-wrap: break-word; background-color: rgb(255, 243, 223); color: rgb(75, 67, 53);\"><div class=\"highlight\" style=\"margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(255, 157, 0) !important;\">小贴士</div><p style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">每年3月15日~6月15日和9月15日~11月15日是春秋防火期，此期间如果自驾到呼伦贝尔市的大部分地区，都要办理防火证；春季防火期是呼伦贝尔市大部分地区一年中相对凋敝的时期，只有5月大兴安岭林区开遍山野的杜鹃花值得前去游览。</p></div></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><h5 style=\"margin: 0px; padding: 0px; font-size: 14px;\">特点：基础消费不高，但是旺季会涨价1倍甚至更多</h5><h5 style=\"margin: 0px; padding: 0px; font-size: 14px;\">住宿</h5><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">呼伦贝尔的消费水平在旺季会比较高。以住宿为例，海拉尔市区的住宿一般标间的价格为200元左右。住在草原上非景点区的蒙古包中，住宿价格在120元~150元左右，还有还价的余地。而旺季，这些住宿地点的房价可能会飙升到400~600元/晚，涨价空间很高。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">住宿相对便宜的区域在额尔古纳的恩和和室韦等俄罗斯民族乡，这里都有挂牌接待游客的家庭旅馆，一般一个床位的价格是30元－40元，早饭是每人10元。</p><h5 style=\"margin: 0px; padding: 0px; font-size: 14px;\">餐饮</h5><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">一些景区、市区的餐饮价格会比较昂贵，一份素菜要价在40多元，荤菜至少要50元以上。所以在选择就餐地点时可以注意考察一下价格。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 12:02:38', '', '0', 'group1/M01/41/40/eBif6Fj5grmAZt39AAERtJM6vOk163.jpg', 'group1/M01/41/40/eBif6Fj5hCqAabUsAAFkbuhfGrQ887.jpg,group1/M01/41/40/eBif6Fj5hC6AECSjAAHaTNSz2EI896.jpg,group1/M01/41/40/eBif6Fj5hDGAAK1SAAYZIbaCCrk916.jpg,group1/M01/41/40/eBif6Fj5hDSAKkEKAAKeOikgR-U955.jpg,group1/M01/41/40/eBif6Fj5hD2AJqGBAASwDjNLKas241.jpg');
INSERT INTO `sys_city` VALUES ('150800', '240', '150000', '巴彦淖尔', 'BYNES', 'B', '107.387489', '40.742766', '', '', '0', '0', '0', '4', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">巴彦淖尔蒙古语意为“富饶的湖泊”，这里自然资源丰富，特色旅游产业发达，交通便利，有“塞上江南，黄河明珠，北方新城，西部热土”的美称。想感受草原城市生活的，巴彦淖尔是个不错的选择。巴彦淖尔市历史文化资源丰富，悠久的文化和特定的地域，造就了巴彦淖尔特有的历史古迹，孕育了灿烂的河套文化和多彩的草原文明。 在这里你可以领略乌兰布和沙漠的浩瀚飘渺，倾听母亲河脉搏的跳动，饱览乌拉特草原的辽阔与广袤，体验河套田原的优美恬静。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，夏季最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-s