INSERT INTO `sys_city` VALUES ('411100', '240', '410000', '漯河', 'ZH', 'Z', '114.016609', '33.580338', 'https://imgb.mofangx.com/test/188a61f29f8045279fa3c4f3b05b10f2.jpg', '金龟山庄,八里城遗址,望奎满族风情园,', '0', '0', '0', '3', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">漯河市是河南省的一个省辖市，位于河南省中南部，伏牛山东麓平原与淮北平原交错地带。漯河市历史悠久，早在新石器时代，先民就在这里定居生息，曾以商贸和水陆运输发达而享誉中原，素有“陆陈集散地”、“水旱码头”之称。 漯河文物资源丰富，以贾湖遗址、彼岸寺经幢、小商桥、三绝碑及授禅台、许慎墓为代表的190余处文物古迹，处处博大精深。境内有淮河的分支水系3，沙河、澧河是北方少有的水景城市、森林城市。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">秋季最佳</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px