INSERT INTO `sys_city` VALUES ('411300', '240', '410000', '南阳', 'NY', 'N', '112.528385', '32.990583', '', '', '0', '0', '0', '3', '0', '0', '1', '<p><strong>简介</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">南阳市位于河南省西南部、豫鄂陕三省交界处，为三面环山、南部开口的盆地，因地处伏牛山以南，汉水以北而得名，南阳又有“南都”、“帝乡”之称，历史上，南阳是古丝绸之路的源头之一。南阳的风俗民情极具特色，艺术丰富多彩，饮食美味，特产众多，同时拥有卧龙岗、水帘洞、太白顶、内乡宝天曼、西峡恐龙遗迹园等多处著名景点，是休闲度假的好去处。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">春秋最佳</p><p><strong>建议游玩天数</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">2天</p><p><strong>当地气候</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">季风大陆半湿润气候</p><p><strong>语言</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">中原官话 南鲁片、信蚌片</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 15:42:32', '', '156', 'group1/M01/46/42/eBif6Fltu3-AClo5AAEZHh1Iy7Y777.jpg', 'group1/M01/46/42/eBif6Fltu9uAS7VkAAH5yEANW0M721.jpg,group1/M01/46/42/eBif6Fltu9-AM19IAACwfCgUqbE965.jpg,group1/M01/46/42/eBif6Fltu-GAH2uIAAHIig3VHxA710.jpg');
