INSERT INTO `sys_city` VALUES ('420700', '240', '420000', '鄂州', 'EZ', 'E', '114.894959', '30.39112', 'https://imgb.mofangx.com/test/3c0b03bbf3dd42e58505d31de0b254ee.jpg', '豫西大峡谷,', '0', '0', '0', '3', '0', '0', '1', '<p><span style=\"font-family: 宋体, SimSun;\">简介</span></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">鄂州市，是湖北省下辖的地级市，地势低平，湖泊密布，盛产稻、麦、棉，是著名的武昌鱼的原产地，是中国佛教净土宗的发源地。境内还有吴王避暑宫、九曲亭、西山古刹、菩萨泉、涵息泉、彭楚藩烈士墓、怡亭铭摩崖石刻等旅游风景区。 鄂州是一座风光秀丽的城市，是长江之滨的旅游城市，山、水、城浑然一体，以西山、梁子湖、红莲湖度假区为代表的自然景点吸引了八方游客。</span></p><p><span style=\"font-family: 宋体, SimSun;\">最佳旅行时间</span></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">每年5月～10月适宜</span></p><p><span style=\"font-family: 宋体, SimSun;\">建议游玩天数</span></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">1天</span></p><p><span style=\"font-family: 宋体, SimSun;\">当地气候</span></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">亚热带季风气候，季风气候明显，冬冷夏热，四季分明</span></p><p><span style=\"font-family: 宋体, SimSun;\">语言</span></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">鄂州话</span></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 17:04:14', '', '0', 'group1/M01/46/4E/eBif6FlvHomADc7BAAErrBTmha8992.jpg', 'group1/M01/46/4E/eBif6FlvIEqACJGqAABsz6j8fD4366.jpg,group1/M01/46/4E/eBif6FlvIE2Aa9_1AACt2clfk2E360.jpg,group1/M01/46/4E/eBif6FlvIE-AcKPvAAC9QWvDXgc878.jpg,group1/M01/46/4E/eBif6FlvIFKAdjAaAABqKCSTktA240.jpg,group1/M01/46/4E/eBif6FlvIFWAUAaJAABpp4SD4Do733.jpg,group1/M01/46/4E/eBif6FlvIFiAK-VQAABR4zDnlDs280.jpg,group1/M01/46/4E/eBif6FlvIFqADD-OAACYf8JdVTU841.jpg,group1/M01/46/4E/eBif6FlvIF2AdmVlAABJIbSxg4c091.jpg,group1/M01/46/4E/eBif6FlvIGCAf7OzAABw5c4mySI389.jpg');
