INSERT INTO `sys_city` VALUES ('429006', '240', '420000', '天门', 'TM', 'T', '113.166558', '30.66372', '', '', '0', '0', '0', '3', '0', '0', '1', '<p><strong>简介</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">天门，古称竟陵，意为“陵之竟也”，1726年（清雍正四年）更名为天门，成为迄今为止中国省直辖市名称笔画最少的城市。从秦始置县算起，天门已有2000多年历史。天门是中国内陆最大的侨乡，典型的江汉水乡，中国唯一兼具东湖、西湖的城市，新型的现代化、工业化园林城市，夜景为江汉之最；是江汉地区教育、商业、医疗和房地产业最发达的地区之一，经济增速位居全省前列，连续8年进入全国最具投资潜力中小城市百强，是全国著名的棉乡、状元之乡 ，中国棉都，中国最大的棉花交易中心，全国产粮大市，中国茶城 ，中国蒸菜之乡 ，中国曲艺之乡 ，全国科技进步先进市 ，武汉城市圈成员城市，武汉城市圈西部综合交通枢纽。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">春夏秋</p><p><strong>建议游玩天数</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">两天</p><p><strong>当地气候</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">亚热带季风气候</p><p><strong>语言</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">中文</p><p><br></p><p><br></p><p><br></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 14:23:28', '', '0', 'group1/M01/46/4A/eBif6Flu-q-AJICTAAEZzNBjeYY334.jpg', 'group1/M01/46/4A/eBif6Flu-r6AUt8UAADQEUGuuo8374.jpg,group1/M01/46/4A/eBif6Flu-sGAV4HuAAH9-XILs3E101.jpg,group1/M01/46/4A/eBif6Flu-sqAJ_1VAAJC4aoaDZA997.jpg');
INSERT INTO `sys_city` VALUES ('429021', '240', '420000', '神农架林区', 'SNJLQ', 'S', '110.675743', '31.744915', '', '', '0', '0', '0', '3', '0', '0', '1', '<div id=\"intro_36741\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">神农架林区位于湖北省西北部，是一个山川交错、峰岭连绵的地方，因华夏始祖神农氏尝百草救民之传说而得名。林区中林密谷深，与世隔绝，完好地保存着洪荒时代的风光，动植物资源极其丰富，野人出没的传说更让它蒙上了一层神秘的色彩。树林的宁静、湖水的波澜、大山的神秘、动物的嬉戏……这里的一切都会让人忘记喧嚣，恍若隔世。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><div class=\"figure\" style=\"margin: 20px 0px; padding: 0px; word-wrap: break-word; font-size: 12px; color: rgb(153, 153, 153); line-height: 20px;\"><img width=\"350\" height=\"200\" src=\"http://n4-q.mafengwo.net/s8/M00/10/D4/wKgBpVWaNu2AJkW3AADXy-WNPyc07.jpeg?imageMogr2%2Fthumbnail%2F%21350x200r%2Fgravity%2FCenter%2Fcrop%2F%21350x200%2Fquality%2F100%7Cimageslim\" style=\"border: 0px; vertical-align: middle; margin-bottom: 8px; max-width: 100%;\"><div style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">图：简介。</div></div></div><div id=\"intro_36757\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">神农架林区旅游开发比较完善，各种旅游接待服务较好，所以语言不会有问题，而且当地方言也不是很难懂。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_36767\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">神农架林区的最佳旅游季节为5-10月，这期间水量充沛泉流清澈，苍山开始滴翠，花儿也朵朵盛开，原始而天然的美景纷呈眼前。加上此时气温稳定，登高眺远，四季之美，皆可网罗，是避暑、寻幽、探秘的最佳出行时间。<br>每年11月到次年4月是神农架林区的冰霜期。如果你喜欢冬日风光，可以选择在这段时间内出游，漫山遍野银装素裹，滑雪、赏雪雕都其乐无穷。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_36768\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">神农架平均海拔1700米，寒冷、潮湿且天气多变，极高气温可达到38.5℃，而极低气温可达-21℃，全年平均气温较低。神农架的夏天早晚比较凉快，中午较为炎热，到神农架旅游须准备必要的防寒衣物，以免着凉。游玩时最好戴好帽子，穿长袖长裤并束紧袖口裤脚，防止被毒虫和蛇咬伤。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-18 17:07:48', '', '188', 'group1/M01/46/46/eBif6Fltzg-ASC5LAACWBaJC_N4109.jpg', 'group1/M01/46/46/eBif6Fltz8mAOHJnAAGJdLV4YkA699.jpg,group1/M01/46/46/eBif6Fltz8-ATdwBAALO5R6bCi8438.jpg,group1/M01/46/46/eBif6Fltz9SAefqeAAt9pPgt2Nw387.jpg,group1/M01/46/46/eBif6Fltz9KAf3rDAA5uIzZBtjY062.jpg,group1/M01/46/46/eBif6Fltz9SARY3HAAIWRG1prgw865.jpg,group1/M01/46/46/eBif6Fltz9iAT9WMAAIBkCi04iA216.jpg,group1/M01/46/46/eBif6Fltz9uASbDLAAHXh9US8JQ053.jpg,group1/M01/46/46/eBif6Fltz-KAYNm6AAHdoiLtKjM309.jpg,group1/M01/46/46/eBif6Fltz-KAHUZ0AAEfGfjBG6k713.jpg');
INSERT INTO `sys_city` VALUES ('430100', '240', '430000', '长沙', 'CS', 'C', '112.938888', '28.228272', 'https://imgb.mofangx.com/test/a12b158a29c9470aa483e6a76aa31d39.jpg', '爱晚亭,橘子洲,天心阁,', '1', '0', '1', '3', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">长沙速览</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">长沙是湖南省省会，山清水秀、气候宜人，有风景独秀“霜叶红于二月花”的岳麓山，有水洲相拥“春来江水绿如蓝”的湘江；有在海内外享有盛名的“世界第八大奇迹”马王堆汉墓、“千年学府”岳麓书院及“三国东吴”简牍。古有云：“惟楚有才，于斯为盛”，在中国近现代史上，长沙更涌现了一批著名的政治家、军事家和文学家。这座有着2000余年悠久文化历史的古城，早在春秋时期，就是楚国雄踞南方的战略要地之一。<br>此外，长沙还是一座时尚动感之都、美食之城。湖南卫视、超女快男风靡全国；街头巷尾随处可见飘香的小吃美食。总之，不论你是喜欢探寻历史还是崇尚青春奔放，亦或只为一饱口腹之欲，在长沙都能找到让你满意的答案。<br><strong>湘江：</strong>为长沙最重要的河流，由南向北贯穿全境，境内长度约75km。湘江自南向北贯穿长沙城区，把城市分为河东和河西两大部分。<br><strong>西岸：</strong>以文化教育为主。其灵魂驻扎之所“岳麓山”以及岳麓书院、科研机构、省内主要的高等学府和博物馆都在这里。<br><strong>东岸：</strong>以商业经济为主，商业区、酒吧街、美食街和酒店均坐落于此。<br><strong>天心区：</strong>是楚汉文化名城长沙的主要发源地，历史文化悠久。天心古阁、白沙古井、湖南第一师范、走马楼三国吴简、宋福王墓和贾谊故居等著名历史景观，及黄兴南路步行商业街、解放西路酒吧文化街、坡子街民俗名食街等商业街区都在该区域内。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><br></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span style=\"color: rgb(51, 51, 51); font-size: 18px;\">最佳旅行时间</span></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">秋季是长沙的最佳旅游时间，可欣赏独立寒秋、湘江北去的橘子洲头，及霜叶红于二月花的岳麓山。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">长沙除盛夏气温较高以外，其余三季均适宜旅游。长沙的春天始于2月中旬后，此时气候多变，游客需多备衣服，以防感冒。11月下旬进入冬季，平均气温在5℃左右，有时会下雪，这时可到山里看冰凌。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span style=\"color: rgb(51, 51, 51); font-size: 18px;\"><br></span></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span style=\"color: rgb(51, 51, 51); font-size: 18px;\">穿衣指南</span></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">长沙春秋两季天气变幻莫测，昼夜温差较大，注意增减衣服。建议穿大衣、呢外套加毛衣、卫衣等服装保暖，带伞预防下雨。夏季炎热，平均温度是23-30℃，建议穿棉麻面料的衬衫、薄长裙、薄T恤等清凉透气的衣服。紫外线强烈，要特别做好防晒工作。长沙冬季平均温度是5-12℃，要穿风衣、毛衣等保暖衣服。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span style=\"color: rgb(51, 51, 51); font-size: 18px;\"><br></span></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span style=\"color: rgb(51, 51, 51); font-size: 18px;\">消费水平</span></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">非“十一”黄金周等重大节假日等热门时间里，两个人2天单纯游长沙，消费约400元基本能解决，主要在交通、住宿方面花费较大。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-14 16:17:30', '', '199', 'group1/M01/46/3B/eBif6FloeY-ANnt8AAEMl2BQaJE499.jpg', 'group1/M01/46/3B/eBif6FlofaaAUAS2AAIi3YxzeVo680.jpg,group1/M01/46/3B/eBif6FlofaWASeLyAAG_MjdWowE180.jpg,group1/M01/46/3B/eBif6FlofcyAbfX5AAyf83PVpLo506.jpg,group1/M01/46/3B/eBif6FlofauAWGT0AAHUotm1uFw113.jpg,group1/M01/46/3B/eBif6FlofbGALKQcAAKlVO-Ybf0266.jpg,group1/M01/46/3B/eBif6FlofbKAcrE8AAFAPWW46Lw258.jpg,group1/M01/46/3B/eBif6FlofbaAfIKUAAHF8gD_bn0293.jpg,group1/M01/46/3B/eBif6FlofbqADfq6AAHYA2-dqP4253.jpg,group1/M01/46/3B/eBif6Flofb2AZ1hiAAHU1L8d3Fc526.jpg');
INSERT INTO `sys_city` VALUES ('430200', '240', '430000', '株洲', 'ZZ', 'Z', '113.13303', '27.828682', '', '', '0', '0', '0', '3', '0', '0', '1', '<p><strong>简介</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">株洲是湖南第二大城市，历史悠久，是中华民族农耕文化发源地之一。株洲旅游资源丰富，除了茶陵、炎陵等革命历史遗迹，神农谷风景区更是株洲最重要的自然景点之一是一个集科学考察和风景观赏于一体的旅游胜地。株洲方特欢乐世界和株洲方特梦幻王国等新兴景点也都是来株洲不得不去的旅游佳地。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">春秋最佳</p><p><strong>建议游玩天数</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">2天</p><p><strong>当地气候</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">亚热带季风气候，四季分明，雨量充沛</p><p><strong>语言</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">湘语长益片、赣语、客家语</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:56:22', '', '211', 'group1/M01/46/45/eBif6FltzEiASaa2AAGvfcDVYNw591.jpg', 'group1/M01/46/45/eBif6FltzROALKiHAAHKp6r5yH0072.jpg,group1/M01/46/45/eBif6FltzROABfNSAADvyFGDEls279.jpg,group1/M01/46/45/eBif6FltzRaAdMgpAAGW-pWb_E0454.jpg');
INSERT INTO `sys_city` VALUES ('430300', '240', '430000', '湘潭', 'XT', 'X', '112.944026', '27.829795', 'https://imgb.mofangx.com/test/89cb1ae830d64adba4ae815345ac6b48.jpg', '韶山滴水洞景区,毛泽东故居,韶山毛泽东同志纪念馆,', '0', '0', '0', '3', '0', '0', '1', '<p><strong><span style=\"font-family: 宋体, SimSun;\">简介</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">湘潭，是红太阳升起的地方，是历史文化名城、湖湘文化的发源地、中国“湘莲之乡”；也是全国人民仰慕的旅游圣地。湘潭是一个历史悠久的古城，名胜古迹颇多。“关圣殿”是清代的名寺，内多木，石雕刻，其中春秋阁前汉白玉镂空龙柱，石龙腾跃，精美绝佳。另外，还有韶山、东台山等美丽的自然风光。</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">最佳旅行时间</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">春秋季节</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">建议游玩天数</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">3天</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">当地气候</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">亚热带季风湿润气候，四季分明</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">语言</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">湘语、普通话</span></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 16:08:12', '', '205', 'group1/M01/46/4C/eBif6FlvEcCAUJcaAAFCLZbIi5k129.jpg', 'group1/M01/46/4D/eBif6FlvEz2AEjhkAAIesAENLQA370.jpg,group1/M01/46/4D/eBif6FlvEz-AUpbwAAKOpKrUAS0505.jpg,group1/M01/46/4D/eBif6FlvE0GAPMjCAAJtDs1bjWE950.jpg,group1/M01/46/4D/eBif6FlvE0SAYto8AAHblGLakII500.jpg');
INSERT INTO `sys_city` VALUES ('430400', '240', '430000', '衡阳', 'HY', 'H', '112.631311', '26.88949', '', '', '0', '0', '0', '3', '0', '0', '1', '', '0', '2015-05-12 16:16:02', '', '2017-04-10 19:52:06', '', '201', 'group1/M01/40/89/eBif6FjrcbKAB8TrAAM96eGqe0o201.jpg', 'group1/M01/40/89/eBif6Fjrcb6ASbxsAAKAVm6tE_w582.jpg,group1/M01/40/89/eBif6FjrccKANBH8AAEyuRgnwlo473.jpg,group1/M01/40/89/eBif6FjrccWAXLowAAEfkQBXP6w806.jpg,group1/M01/40/89/eBif6FjrcciAWmBXAACzs2sM1Z0141.jpg,group1/M01/40/89/eBif6FjrcdCATs1WAAFqwakwERg510.jpg,group1/M01/40/89/eBif6FjrcdaAE8bJAAI73-APpXA601.jpg');
INSERT INTO `sys_city` VALUES ('430500', '240', '430000', '邵阳', 'SY', 'S', '111.468121', '27.239146', '', '', '0', '0', '0', '3', '0', '0', '1', '<p><strong><span style=\"font-family: 宋体, SimSun;\">简介</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">邵阳，旧称宝庆，是中国湖南省下辖的一个地级市，自古为交通要道，商埠中心，经济发达，文化昌明，境内发掘多处文化遗址。邵阳自然风光绮丽，美不胜收，主要景区有崀山旅游风景区、白水洞风景名胜区、城步南山牧场、云山国家森林公园、黄桑自然保护区、虎形山-花瑶、隆回魏源故居等。</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">最佳旅行时间</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">夏秋最佳</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">建议游玩天数</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">1天</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">当地气候</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">亚热带季风性湿润气候</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">语言</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">湘语（娄邵片）、赣语（洞绥片）</span></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 15:38:25', '', '0', 'group1/M01/46/4C/eBif6FlvCvmABo93AAFfJefu7F4225.jpg', 'group1/M01/46/4C/eBif6FlvDDGAZj0VAAEPpraAvRs360.jpg,group1/M01/46/4C/eBif6FlvDC-APwyiAAKErZcV6uo751.jpg,group1/M01/46/4C/eBif6FlvDDWAcE6BAAGFv75B41I341.jpg,group1/M01/46/4C/eBif6FlvDDiAakFGAADi3KVVPGw940.jpg');
INSERT INTO `sys_city` VALUES ('430600', '240', '430000', '岳阳', 'YY', 'Y', '113.128771', '29.356151', 'https://imgb.mofangx.com/test/02f9c7e04be2464cb23c6b4d4637328a.jpg', '岳阳楼,岳阳张谷英村,洞庭湖,', '0', '0', '0', '3', '0', '0', '1', '<p><strong>简介</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">岳阳市位于中国湖南省东北部，东倚幕阜山，西抱洞庭湖，北枕长江，区位优越，风景秀丽，土地肥沃，物产丰富，素有“鱼米之乡”的美誉。岳阳矿产资源丰富，矿藏矿点200多处，其中钒矿蓄量居亚洲之冠。岳阳集名山、名水、名楼、名人、名文于一体，是中华文化重要的始源地之一。其中有岳阳楼、慈氏塔、鲁肃墓、岳州文庙、洞庭湖中的君山和扁山、城南的南湖公园为著名游览地。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">5月至10月最佳</p><p><strong>建议游玩天数</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">1-2天</p><p><strong>当地气候</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">亚热带季风气候，气候湿润，四季分明</p><p><strong>语言</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">岳阳话</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:51:30', '', '209', 'group1/M01/46/45/eBif6Fltyw6AdR4IAAF7QTKKmr4394.jpg', 'group1/M01/46/45/eBif6Flty-eAdx0FAAGckdfPfew980.jpg,group1/M01/46/45/eBif6Flty-aAPwwFAAGIEYp2xnM975.jpg,group1/M01/46/45/eBif6Flty-qAKhW4AAGZOCnV1pc111.jpg');
INSERT INTO `sys_city` VALUES ('430700', '240', '430000', '常德', 'CD', 'C', '111.698784', '29.031654', '', '', '0', '0', '0', '3', '0', '0', '1', '<p><strong>简介</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">常德位于湖南省洞庭湖西畔、武陵山下，紧邻世界著名风景区张家界国家森林公园，特殊的地理位置，构造了常德山青水美的自然风光。境内有国家4A级风景名胜区桃花源、千年佛教圣地夹山寺、湖南屋脊壶瓶山、常德市柳叶湖、省级风景名胜区嘉山、载入吉尼斯世界纪录的“中国常德诗墙”等景区景点。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">春秋时节最美，春季有桃花节</p><p><strong>建议游玩天数</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">2天</p><p><strong>当地气候</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">中亚热带湿润季风气候</p><p><strong>语言</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">西南官话-常德话</p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 15:20:11', '', '0', 'group1/M01/46/4B/eBif6FlvBqyAYysmAADzFDdhxwk545.jpg', 'group1/M01/46/4C/eBif6FlvB_WAMQ5cAADQQ8akwxs964.jpg,group1/M01/46/4C/eBif6FlvB_SARHX1AAHfMHwGIsQ323.jpg,group1/M01/46/4C/eBif6FlvB_eAUwVoAAFxkGPbhgY750.jpg,group1/M01/46/4C/eBif6FlvB_qAEALcAAGSjZNbKMU008.jpg');
INSERT INTO `sys_city` VALUES ('430800', '240', '430000', '张家界', 'ZJJ', 'Z', '110.85941', '29.57977', 'https://imgb.mofangx.com/test/f8a0941099b84321bc22ae1613c0a0ee.jpg', '金鞭溪,袁家界,张家界核心景区武陵源,', '0', '0', '0', '3', '0', '0', '1', '<h1 class=\"h1\" style=\"margin: 0px; padding: 0px; font-size: 24px; font-weight: normal; color: rgb(51, 51, 51); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">张家界速览<br><div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px;\"><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">张家界位于湖南西北部，属武陵山脉腹地。因旅游而建、因旅游而兴的张家界市，拥有令世人惊叹的风光。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">俗话说“九寨沟看水，张家界看山”。武陵源核心景区中三千座石峰拨地而起，八百条溪流蜿蜒曲折，来张家界旅游观光的人，无不被武陵源奇特的砂岩峰林地貌和壮丽的喀斯特景观所倾倒，仿佛置身于神山仙境之中，故而张家界有“中国山水画的原本”、“缩小的仙境，放大的盆景”的美誉。2010年首部3D电影《阿凡达》，更是让其中的电影取景地——张家界的美景享誉世界。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">4-6、9-11月是张家界的最佳旅游时间。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">4-6月：正值张家界的春天，落英缤纷，溯金鞭溪，游十里画廊，探黄龙洞，体会武陵人进入桃花源的惊喜；</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">9-11月：秋天的张家界天高云淡，层林尽染，此时正是猕猴桃、蜜橘等美味水果成熟的时节。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">夏季高温多雨，冬季气候寒冷，因此气候舒适度稍逊于春秋。</p><div class=\"tips\" style=\"margin: 20px 0px 0px; padding: 15px 20px; word-wrap: break-word; background-color: rgb(255, 243, 223); color: rgb(75, 67, 53);\"><div class=\"highlight\" style=\"margin: 0px; padding: 0px; word-wrap: break-word; color: rgb(255, 157, 0) !important;\">小贴士</div><p style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">雨天山路湿滑，雨水和大雾也会导致视线不清，最好不要在雨中冒险登山；</p><p style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">黄金周和暑假期间是旅游旺季，各种服务价格上涨，人多排队久，交通、食宿也多有不便。</p></div></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">3-5天。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">到张家界通常都是游国家森林公园，景区面积大，景点多，至少3天为宜，5天可游较为透彻。天门山风景区半天到1天即可。如时间充裕还可顺道游附近的宝峰湖和黄龙洞。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">张家界因为旅游城市，景区内的整体消费均比市区内要高些。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">餐饮：在市内吃饭人均约30元/顿，若点张家界名吃“三下锅”消费会高些，人均70-100元/顿；在景区内想要吃好些人均约50/顿。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">住宿：以最集中的森林公园内住宿为例，山上平时标间60-100元/晚，旺季时200元以上/晚。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">交通：在市内主要是打的或乘公交车，费用不高；若在景区内搭乘索道费用会较高。</p></div></h1>', '0', '2015-05-12 16:16:02', '', '2017-04-10 17:10:19', '', '210', 'group1/M01/40/88/eBif6FjrR5WAP357AAMuh4siQ1c446.jpg', 'group1/M01/40/88/eBif6FjrR52AbTJzAAMI1iHgf6E012.jpg,group1/M01/40/88/eBif6FjrR6KAcHxUAARWLnyhxeQ227.jpg,group1/M01/40/88/eBif6FjrR6WAH27UAAK2qF5MWMM596.jpg,group1/M01/40/88/eBif6FjrR6qAfkX4AAKXzSwKfkQ044.jpg,group1/M01/40/88/eBif6FjrR7WANqRsAAHWBrOWV5U691.jpg,group1/M01/40/88/eBif6FjrR9WAVwtGAAQFsUl-hzU019.jpg');
INSERT INTO `sys_city` VALUES ('430900', '240', '430000', '益阳', 'YY', 'Y', '112.355131', '28.554973', '', '', '0', '0', '0', '3', '0', '0', '1', '<p><strong><span style=\"font-family: 宋体, SimSun;\">简介</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">益阳市位于湘中偏北，跨越资水中下游，处沅水、澧水尾闾，环洞庭湖西南，不但矿藏和水资源丰富，还有大量的野生动物资源，加上优越的气候使得益阳成为名副其实的“鱼米之乡”和“茶竹之香”。益阳历史悠久，人文环境优美，文化历史旅游资源众多。主要旅游景点有，古代湖南四大书院之一—龙洲书院，东晋古刹，栖霞寺，凤凰山，古老的茶马古道景区等。</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">最佳旅行时间</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">秋冬季最佳</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">建议游玩天数</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">1天</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">当地气候</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">亚热带大陆性季风湿润气候</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">语言</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">湘语</span></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 15:59:47', '', '0', 'group1/M01/46/4C/eBif6FlvD-GAQoc8AAFEw0UuKGI246.jpg', 'group1/M01/46/4C/eBif6FlvET-AMm3RAACmwaB9W7s149.jpg,group1/M01/46/4C/eBif6FlvEUKAbMXAAAET6a0rXNA212.jpg,group1/M01/46/4C/eBif6FlvEUGAEQZ-AAF3ekmeWVQ916.jpg,group1/M01/46/4C/eBif6FlvEUSAAnuAAAFxRXudVdA374.jpg');
INSERT INTO `sys_city` VALUES ('431000', '240', '430000', '郴州', 'CZ', 'C', '113.014984', '25.770532', '', '', '0', '0', '0', '3', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">郴州位于湖南省东南部，是拥有两座中国优秀旅游城市的地级市和中国温泉之乡，是走向世界的“南大门”。这里的自然景观甚多，如：中国原始生态第一山——莽山、让世界呼吸的东江湖、苏仙岭等，这都是国家级风景名胜区。您还可以来永兴板梁古村、桂阳阳山古村。您更不能错过的是这里的温泉，郴州地热资源丰富，温泉的矿物质多，对人体十分有益。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">春秋最佳</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102);