INSERT INTO `sys_city` VALUES ('431100', '240', '430000', '永州', 'YZ', 'Y', '111.613464', '26.419852', 'https://imgb.mofangx.com/test/86d2526d808648a189a279953e4ed7cb.jpg', '萍岛,柳子庙,浯溪碑林,', '0', '0', '0', '3', '0', '0', '1', '<p><span style=\"font-family: 宋体, SimSun;\"><strong></strong></span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">简介</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">永州市位于湖南省南部，是一座山水江河交融的城市，一座充满生机活力的城市，一座 文化底蕴深厚的城市，是中国山水诗的发祥地、瑶族文化、楚文化发祥地之一。</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">最佳旅行时间</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">四季皆宜</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">建议游玩天数</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">2天</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">当地气候</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">亚热带季风性湿润气候</span></p><p><strong><span style=\"font-family: 宋体, SimSun;\">语言</span></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"font-family: 宋体, SimSun;\">永州话</span></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 16:15:30', '', '0', 'group1/M01/46/4D/eBif6FlvE6SAZQWwAADPg9gEDxo063.jpg', 'group1/M01/46/4D/eBif6FlvFPKAOmA2AAEbXG7Nw3A165.jpg,group1/M01/46/4D/eBif6FlvFPOAABj1AAFV0CDfVoA152.jpg,group1/M01/46/4D/eBif6FlvFPWAPtuXAAFHh0wuH1s931.jpg,group1/M01/46/4D/eBif6FlvFPmAZmdEAADUh9ff69c382.jpg');
INSERT INTO `sys_city` VALUES ('431200', '240', '430000', '怀化', 'HH', 'H', '110.001596', '27.569889', 'https://imgb.mofangx.com/test/6ec8c10da0924b5f86faa573b43c4978.jpg', '龙津风雨桥,受降纪念馆,万佛山,', '0', '0', '0', '3', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">怀化旅游区以怀化市和芷江、沅陵为中心，拥有不少风光名胜和历史文化遗迹。万佛山的丹霞地貌、五强溪浩淼泽国、“第二兵马俑”美誉的秦代“黔中郡”古城和被赞誉为“楚南上游第一胜迹”的芙蓉楼，无不钟灵毓秀，令人流连忘返。此外，到怀化还能体会侗族赶歌节、芦笙舞、斗鸡和织锦、蜡染等浓厚的民族特色和风土人情。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px