INSERT INTO `sys_city` VALUES ('445100', '240', '440000', '潮州', 'CZ', 'C', '116.621468', '23.657626', '', '', '0', '0', '0', '2', '0', '0', '1', '<section data-role=\"outer\" label=\"Powered by 135editor.com\" style=\"font-family:微软雅黑;font-size:16px;\"><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">潮州，位于广东东部，东与福建省接壤，是国家历史文化名城、潮州文化的重要发源地，素有“岭海名邦”、“岭东首邑”等美誉。潮州有众多属于自己的独特文化：潮绣、潮州戏、潮州木雕、潮州菜、潮州功夫茶、潮州民居等。潮州旅游资源也极其丰富，有新旧潮州八景，潮州以其独特的魅力吸引着众多游客前来游玩。</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">9月-10月</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">建议游玩天数</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">1天</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">当地气候</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">亚热带海洋性季风气候</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">语言</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">潮州话、客家语</p><p><br></p></section>', '0', '2015-05-12 16:16:02', '', '2017-04-15 15:18:48', '', '77', 'group1/M01/40/A7/eBif6FjxySWACrHnAAG1tFHr9MA637.jpg', 'group1/M01/40/A7/eBif6FjxySeABtrJAAI3RQEc3Xg934.jpg,group1/M01/40/A7/eBif6FjxySmACOTgAAHgj9-ciqQ680.jpg,group1/M01/40/A7/eBif6FjxyTyATyZ9AAKPnIUyRbo035.jpg,group1/M01/40/A7/eBif6FjxyS6AYI2eAAJkfa-9x7Y071.jpg,group1/M01/40/A7/eBif6FjxyTCAOIdkAALBlHxRfiw575.jpg,group1/M01/40/A7/eBif6FjxyTOAKWP6AALgZX7NZTE666.jpg,group1/M01/40/A7/eBif6FjxyTWAUnSiAAG6LLsxC-U745.jpg,group1/M01/40/A7/eBif6FjxyTeAGqr1AAI8UO_ZWro448.jpg,group1/M01/40/A7/eBif6FjxyTmAffWjAAHD3-tNK8g395.jpg');
INSERT INTO `sys_city` VALUES ('445200', '240', '440000', '揭阳', 'JY', 'J', '116.372708', '23.549701', 'https://imgb.mofangx.com/test/dcd7a02d1fa84c8b9d7bfc19fc754060.jpg', '桑浦山,', '0', '0', '0', '2', '0', '0', '1', '<section data-role=\"outer\" label=\"Powered by 135editor.com\" style=\"font-family:微软雅黑;font-size:16px;\"><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">揭阳市是广东的一个地级市，位于广东省东部榕江中游，潮汕平原的中部，其东北接潮州市潮安县，东南接汕头市，西邻汕尾市，南濒南海，北靠梅州市。陆地面积5240平方公里，海域面积7689平方公里，大陆海岸线长82公里，沿海岛屿30多个，内陆江河主要有榕江、龙江和练江三大水系。是潮汕文化的发源地，粤东古邑，广东省历史文化名城，全国著名侨乡，港澳台同胞和旅外华侨320多万人，遍居世界各地，还有归侨、侨眷180万人。揭阳地势自西向东倾斜，低山高丘与谷地平原交错相间分布不均，西北部和西南部多为丘陵、山地，中部、南部和东南部都是广阔肥沃的榕江冲积平原和滨海沉积平原，揭阳母亲河榕江是广东粤东地区第二大河流。</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">四季皆宜，春秋最佳。</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">建议游玩天数</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">1天</p><section class=\"_135editor\" data-id=\"t3\" style=\"position: static; border: 0px none; padding: 0px; box-sizing: border-box;\"><section style=\"margin:10px 0px;\"><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">当地气候</span></p></section></section></section><p style=\"line-height: 1.75em; text-align: justify; color: rgb(127, 127, 127); font-size: 14px; margin-bottom: 10px;\">亚热带季风气候</p><p><br></p></section>', '0', '2015-05-12 16:16:02', '', '2017-04-15 16:48:26', '', '84', 'group1/M01/40/A7/eBif6Fjx3hiAfFdyAAFKbLeSAnc123.jpg', 'group1/M01/40/A7/eBif6Fjx3iCAcINKAAE5vifrSG8448.jpg,group1/M01/40/A7/eBif6Fjx3iyAA8O_AAHOgCT_ums614.jpg,group1/M01/40/A7/eBif6Fjx3i-AQL4RAAFoPAmR3IA023.jpg,group1/M01/40/A7/eBif6Fjx3jKAcVcZAAFJYvQRluM959.jpg,group1/M01/40/A7/eBif6Fjx3jSAGUrqAAFoR1d40Fc017.jpg,group1/M01/40/A7/eBif6Fjx3jeAT3LCAADvGIqc7fc534.jpg,group1/M01/40/A7/eBif6Fjx3j2AAfImAAFULLECWt0555.jpg,group1/M01/40/A7/eBif6Fjx3j-ANKyDAAHQOfl4JhA659.jpg,group1/M01/40/A7/eBif6Fjx3kGASifgAADo57gnql0507.jpg');
INSERT INTO `sys_city` VALUES ('445300', '240', '440000', '云浮', 'YF', 'Y', '112.044491', '22.915094', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">云浮，又被称为广东大西关，是连接广东珠三角和大西南的枢纽，系著名历史文化、旅游风景名城，全国有名的“石材王国”，素有“硫都”和“石都”之称，有“沙糖桔之乡”的称誉。“城中有山，山中有水，绿树花香，山水相映”是云浮的特色。 云浮旅游资源得天独厚，优美的自然景观与丰富的人文景观交相辉映，游览佳境不胜枚举。蟠龙洞、凌霄岩、星岩古洞、西江金鱼沙、国恩寺、龙山温泉等是远近闻名的旅游胜地。新兴县的国恩寺是六祖惠能禅师开山创建和圆寂之所，迄今已有1300年历史，被列为省的重点文物保护单位。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">四季皆宜，秋冬最佳。</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">1天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带季风性气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">粤语、客家语</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 15:38:21', '', '93', 'group1/M01/46/39/eBif6FloXiaAMK_XAADKyL7bgUc341.jpg', 'group1/M01/46/3A/eBif6FloYZeAOujnAAGJincK3FQ823.jpg,group1/M01/46/3A/eBif6FloYZuAb4BjAAFdvCdY-mQ756.jpg,group1/M01/46/3A/eBif6FloYZqAeYanAAEb17y0hmg960.jpg,group1/M01/46/3A/eBif6FloYZ6Ac19tAAG4EO-To0Y606.jpg,group1/M01/46/3A/eBif6FloYaKAHzhTAAIBlcDkRLk412.jpg,group1/M01/46/3A/eBif6FloYaaAYPYSAAITpn4dpTI058.jpg,group1/M01/46/3A/eBif6FloYaqAHlNwAADi4MH45lY219.jpg,group1/M01/46/3A/eBif6FloYbKALtCAAAGGj4GZq4U050.jpg,group1/M01/46/3A/eBif6FloYbWAPaJeAAHAzixj0Ls939.jpg');
INSERT INTO `sys_city` VALUES ('450100', '240', '450000', '南宁', 'NN', 'N', '108.36637', '22.817746', 'https://imgb.mofangx.com/test/c65d0d9ecadf4637b04d4036272c7b6d.jpg', '南宁大明山,南宁南湖公园,青秀山,', '1', '0', '1', '2', '0', '0', '1', '<p><strong>简介</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">南宁是广西壮族自治区首府。古代属于百越之地，距离现在有1680年历史，唐朝贞观八年，唐太宗把这里定名为邕州，南宁的简称“邕”由此而来，元朝泰定元年，中央政府为取南疆安宁，而定名为南宁。<br></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">绿色是南宁的标志，半城绿色半城楼，因此被称为“中国绿城”，还获得了“联合国人居奖”。初来乍到，也许并未发觉有何亮点，但定有不少人感慨，这儿的人怎么走路都这么慢呀！确实，“慢”是这里的生活状态，也是一种生活态度。漫步穿梭于古镇的石板路上，在骑楼老巷间寻觅舌尖上的美食，依偎在公园长椅上看夕阳斜射波光粼粼，亦或感受其渐露头角的时尚范儿……让心情放缓，让脚步慢下来，你会发现，这个包容的城市带来的惊喜。在那满眼舒心的绿意中，估计你也忍不住想，“等到风景都看透，谁陪你看细水长流”。<br>中山路：著名美食街区，能看到古老的骑楼建筑遗址和基督教堂。<br>朝阳广场：俗称市中心，也是南宁人气最旺的商圈，有万达广场、百货大楼、沃尔玛等，临近火车站。<br>琅东片区：新城区，有广西最大的消费中心——万象城；还有南宁国际会展中心、航洋国际、南湖公园、青秀山景区等。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><strong>最佳旅行时间</strong><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">南宁位于北回归线以南，夏长冬短，春秋足迹只有短暂停留。因一年四季都是满眼的绿，任何时候都适合旅游观光，尤以3-11月最适宜旅游。其中，2-3月：春花烂漫，金茶花公园、花花大世界等公园都是“摄友”的寻花处。12-2月：想看雪的可攀南宁市郊的大明山，这里是南宁唯一有下雪的地方，在北回归线上很是奇特，可观雾凇和雪景。<br></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><strong><br></strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><strong>穿衣指南</strong><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">南宁四季气候温和，3月开始回暖，此时易遇上回南天，整月会是几乎不断的绵绵细雨，可能北方来的会不太习惯，随身带伞。7-8月为盛夏，必需清凉上阵，至11月底还能穿短袖薄衫，但此时早晚温差大，得多备一件外套。夏季夜晚略有蚊虫，不适的可换长裤，或随身带小瓶花露水喷雾。12-2月为冬季，若是碰上阴雨天，则会较冷，需多穿厚衣。<br></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><br></p><p><strong>语言</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">除了南宁平话、南宁白话外，也许最为人知晓的便是“南普”了，学名：南宁普通话。即以北方话为基础方言，以典范的现代白话文作品为语法规范，带有明显南宁白话、平话、壮话口音，通行于南宁的共同语言。<br></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">南宁人爱在一句话之后加上语气词，其中“捏”字最为广泛，若是习惯带上这个尾巴，那恭喜你已经被同化不少了。可参见最具代表性的人物是谐星杨建伟，他自己创作的“南普”小品《夫妻应聘》、《旅店夜话》在第八届中央电视小品大赛中获得了第五名的好成绩。</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-14 17:57:00', '', '0', 'group1/M01/46/3C/eBif6FlolA-AcoB0AAIPCsHvzds556.jpg', 'group1/M01/46/3C/eBif6FlolQ2AYzN9AAGqhHE1c_Q441.jpg,group1/M01/46/3C/eBif6FlolQ-AbQl9AAFFnj2pRns471.jpg,group1/M01/46/3C/eBif6FlolRCAHxJeAAHpwk-DVjU062.jpg,group1/M01/46/3C/eBif6FlolROANU_OAAH6yC2f4sY967.jpg,group1/M01/46/3C/eBif6FlolRaAC1EhAADDQJgoAYI712.jpg');
INSERT INTO `sys_city` VALUES ('450200', '240', '450000', '柳州', 'LZ', 'L', '109.428055', '24.326513', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">柳州是位于广西壮族自治区中北部的城市，史称“龙城”，地形为“三江四合， 抱城如壶”，亦称“壶城”。早在5万年前就有“柳江人”生活在这里了，从建城至今已有两千一百多年的历史，柳州属于典型的喀斯特地貌，形成了“拔地奇峰画卷开”的山水特点。这里石山奇特秀美，岩洞瑰丽神奇，泉水幽深碧绿，江流弯曲明，具有良好的人文历史景观和丰富的旅游资源，是国家甲级旅游城市和历史文化名城。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">5-10月最佳</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">2天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">中亚热带季风气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">西南官话-桂柳片、麼个话等</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:39:53', '', '0', 'group1/M01/46/44/eBif6FltyGSAW89EAADTrdFFA18060.jpg', 'group1/M01/46/45/eBif6FltySiAVoE4AAJNQj9jxVo676.jpg,group1/M01/46/45/eBif6FltySqAeucDAAIGsSr4Cro942.jpg,group1/M01/46/45/eBif6FltyS-ALo57AAhmHfNM-GQ526.jpg,group1/M01/46/45/eBif6FltyTCAPromAAa0IWNzids069.jpg');
INSERT INTO `sys_city` VALUES ('450300', '240', '450000', '桂林', 'GL', 'G', '110.292449', '25.275231', 'https://imgb.mofangx.com/test/71579e1e0c24495e9a5dea30211fbebb.jpg', '银子岩,古东瀑布,印象刘三姐,', '0', '0', '0', '2', '0', '0', '1', '<p><strong>桂林速览</strong></p><p><a href=\"http://n4-q.mafengwo.net/s8/M00/AA/AF/wKgBpVWolDiAQZDmAAQ16x541bE108.png\" target=\"_blank\" title=\"点击查看原图\" style=\"color: rgb(153, 153, 153); cursor: pointer;\" _href=\"http://n4-q.mafengwo.net/s8/M00/AA/AF/wKgBpVWolDiAQZDmAAQ16x541bE108.png\"><img src=\"http://n4-q.mafengwo.net/s8/M00/AA/AF/wKgBpVWolDiAQZDmAAQ16x541bE108.png\" style=\"border: 0px; vertical-align: middle; margin-bottom: 8px;\" _src=\"http://n4-q.mafengwo.net/s8/M00/AA/AF/wKgBpVWolDiAQZDmAAQ16x541bE108.png\"></a></p><p style=\"text-indent: 2em;\"><span style=\"text-indent: 2em;\">桂林位于广西壮族自治区，在“湘西走廊”的南端，自古享有“山水甲天下”之美誉，以山水、人情、风俗和悠闲舒适的生活而闻名。</span></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">桂林风景秀丽，有以漓江风光和喀斯特地貌为代表的山水景观：这里的山，平地拔起，千姿百态；漓江的水，蜿蜒曲折，明洁如镜。此外，桂林还有壮、瑶、苗、侗等十多个少数民族独特的民俗风情文化，为这篇土地更添万千风情。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">桂林是中国乃至世界重要的旅游城市，是中国著名的风景游览城市和历史文化名城，是最适宜自助游的城市之一。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">每年9-11月是游览桂林的最佳时间。此时降雨少，气候宜人，江水水量适中，适合泛舟漓江。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">“五一”和“十一”两个黄金周，也是旅游的高峰期，住宿预订和交通也较紧张，价格比平时也会有所上涨，建议最好避开这个时间。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\"><span class=\"highlight\" style=\"text-indent: 2em; color: rgb(255, 157, 0) !important;\">4-5月：</span><span style=\"text-indent: 2em;\">进入雨季，经常会下雨。漓江水位会涨，水流急且浑浊，不利于看倒影，不利于户外活动。但此时气温不高，下雨时较凉爽，可以看到“烟雨漓江”的景色。</span></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">6-8月：</span>雨季中期至末期，会有短时间强降雨。湿度大，气温高，比较闷热。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">9-11月：</span>秋天是游览桂林的最好时机。</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">12-2月：</span>旱季，其中1-2月为枯水期，漓江可能封航。桂林北部山区比较寒冷，室内温度低，体感阴冷。</p><p><strong>建议游玩天数</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">&nbsp; &nbsp; &nbsp; &nbsp; 2-4天</p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">最基础的行程安排是：第一天游桂林市区；第二天去可以泛舟漓江，晚上逛阳朔；第三天沿漓江逆流而上到杨堤，坐车回桂林。如果想游览龙脊梯田，需要再安排1天。</p><p><strong>消费水平</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">非“十一”黄金周等重大节假日等热门时间里，主要在交通、住宿及景点方面花费较大。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">景点：</span>门票是主要花费，1人3天的行程，游览了漓江、象鼻山、龙脊梯田、遇龙河等地大约花费了700元。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">餐饮：</span>餐饮的花费弹性较大。最具特色桂林米粉，价格低廉的只要5元左右。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">住宿：</span>青旅均价为80元，三星酒店均为价为180元，四星级酒店均价为280元。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">交通：</span>桂林至阳朔的轮渡在200+元/单程；桂林市内公交车为1-2元；出租车起步价2公里以内白天9元，夜间11元。</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-14 17:49:16', '', '102', 'group1/M01/46/3C/eBif6FlokWiAC3gxAAJPMMcnUt4447.jpg', 'group1/M01/46/3C/eBif6FlokvOADdVxAAE7Ly3wtIA804.jpg,group1/M01/46/3C/eBif6FlokvSAGCpTAAFipa8BkEQ922.jpg,group1/M01/46/3C/eBif6FlokvaAat_4AAIEGfGCRvQ938.jpg,group1/M01/46/3C/eBif6FlokvmAFhW0AAIK6rMcgNs021.jpg');
INSERT INTO `sys_city` VALUES ('450400', '240', '450000', '梧州', 'WZ', 'W', '111.279115', '23.476962', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">梧州位于广西东部，与粤、港、澳一水相连，享有“绿城水都”和“世界人工宝石之都”的美誉。梧州历史久远，文化积淀深厚，是一座拥有4000多年历史的古老城市。梧州有众多宗教建筑，是佛教、道教、儒教、天主教、基督教的宗教之地；梧州还是一个自然生态考察之地，爽岛旅游度假区湖光山色，交相辉映。在梧州，你还可以来一次现代农业观光旅，欣赏迷人的田园风光。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">四季皆宜</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">1-2天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带季风气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">梧州话</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:47:54', '', '0', 'group1/M01/46/45/eBif6FltyqiAIH2SAAC8iBnnMkU725.jpg', 'group1/M01/46/45/eBif6FltyxiAdxZTAAEzoEk-onI629.jpg,group1/M01/46/45/eBif6FltyxyAWPYRAAYdxlAbcoo917.jpg,group1/M01/46/45/eBif6Fltyx6AXLR8AAEtAP9P6Wc448.jpg,group1/M01/46/45/eBif6FltyyCAWtbjAAESB6bZ5VI074.jpg');
INSERT INTO `sys_city` VALUES ('450500', '240', '450000', '北海', 'BH', 'B', '109.120244', '21.481256', 'https://imgb.mofangx.com/test/7f0c3ac44d3b4c26b6bb4e99f19b7576.jpg', '涠洲岛,五彩滩,北海老街,', '0', '0', '0', '2', '0', '0', '1', '<p style=\"font-weight: normal;\"><strong>北海速览</strong></p><p style=\"text-indent: 0px;\"><span style=\"text-indent: 2em; font-size: 10pt;\"><b>&nbsp; &nbsp; &nbsp; &nbsp;</b>北海市地处广西南端，北部湾东北岸。市区南北西三面环海，有涠洲（24.74 平方公里）、斜阳（1.8 平方公里）二个海岛，涠洲岛距市区大约20.2 海里。这是个浪漫的城市，风光旖旎，气候宜人。阳光、沙滩、海水是北海给人的最深印象。如果说，温暖的阳光和洁净的海水在其它地方未必稀罕，那么有“天下第一滩”美誉的北海银滩，毫无疑问是北海市一张叫得最响的“旅游王牌”，沙软如毯的银滩铺成直通大海的走廊。特别一提的是，被中国国家地理评为“中国最美的小岛”——北海涠洲岛，其梦幻如画的景致，自然淳朴的风情，使之成为时下最热门的广西旅游胜地。</span></p><p style=\"text-indent: 0px;\"><strong style=\"text-indent: 2em; font-size: 10pt;\">银滩：</strong><span style=\"text-indent: 2em; font-size: 10pt;\">银滩面积超过大连、烟台、青岛、厦门和北戴河海滨浴场沙滩的总和，而平均坡度仅为0.05。沙滩均由上等的石英砂堆积而成，阳光下的沙滩会泛出银光，故称银滩。北海银滩以 “滩长平、沙细白、水温净、浪柔软、无鲨鱼”为特点，有“天下第一滩”的美誉。</span></p><p style=\"text-indent: 0px;\"><strong style=\"text-indent: 2em; font-size: 10pt;\">老街：</strong><span style=\"text-indent: 2em; font-size: 10pt;\">北海老街始建于1883 年，已有百年历史。沿街全是中西合璧骑楼式建筑，临街两边墙面的窗顶多为卷拱结构，卷拱外沿及窗柱顶端都有雕饰线，线条流畅、工艺精美。</span></p><p style=\"text-indent: 0px;\"><strong style=\"text-indent: 2em; font-size: 10pt;\">北部湾广场：</strong><span style=\"text-indent: 2em; font-size: 10pt;\">地处北海最繁华的市中心，几乎是去往各个景点的公交车中转地。</span></p><p style=\"text-indent: 0px;\"><strong style=\"text-indent: 2em; font-size: 10pt;\">侨港镇：</strong><span style=\"text-indent: 2em; font-size: 10pt;\">这里是著名的糖水小吃街。侨港是当年联合国安置难民的示范点，镇上大部分的居民都有越南背景，既会说越南话也会本地话，浓郁的越南风情和海滩渔港形成了小镇独特的魅力。</span></p><p style=\"text-indent: 0px;\"><strong style=\"text-indent: 2em; font-size: 10pt;\">涠洲岛：</strong><span style=\"text-indent: 2em; font-size: 10pt;\">涠洲岛是广西最大的海岛，也是我国最大、最年轻的死火山岛。岛上惊涛拍岸，岩奇洞深，风光旖旎，民风淳朴，故素有“大蓬莱”仙岛之称。</span></p><p style=\"font-weight: normal;\"><strong>最佳旅行时间</strong></p><p style=\"font-weight: normal; margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">北海的海水年平均温度较高，春、夏、秋三季都可以游泳，每年4-11 月是北海旅游的最好时间（其中暑期为旅游高峰期），这期间阳光充足，海水温暖，银滩游人无数、乐趣无穷。涠洲岛上更是赏日出、观海景、游海岛的最佳时节。出行前要注意是否有台风天气。</p><p style=\"font-weight: normal;\"><strong>消费水平</strong></p><p style=\"font-weight: normal; margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">非7、8 月暑期或国庆等重大节假日类热门时间里，两个人3天单纯游北海，消费不高，如不吃昂贵海鲜，约1500元基本能解决；若是要上涠洲岛，住宿条件不错的，3000 元左右已可满足，主要在吃海鲜及涠洲岛渡轮及上岛费方面花费较大。</p><p style=\"font-weight: normal;\"><strong>穿衣指南</strong></p><p style=\"font-weight: normal; margin-top: 15px; margin-bottom: 15px; text-indent: 2em;\">夏季的北海很热，清凉上阵即可，游泳要备好游泳衣、遮阳帽、太阳镜、沙滩裤、凉鞋或拖鞋等。建议多带一件防晒衣或白衬衫，炎阳下防晒伤，或是11 月时早晚温差大，清晨、夜晚或下雨时微凉可披上。一定要带防晒霜！</p>', '0', '2015-05-12 16:16:02', '', '2017-07-14 17:27:26', '', '99', 'group1/M01/46/3B/eBif6FlojJGAIkZ2AAGA2BuJGwM189.jpg', 'group1/M01/46/3C/eBif6FlojdyAXVEMAAEfbIscyRw493.jpg,group1/M01/46/3C/eBif6FlojeGAF1o_AAOYYBiMY-4435.jpg,group1/M01/46/3C/eBif6Flojg6AGQTUAAG4cEpypME497.jpg');
INSERT INTO `sys_city` VALUES ('450600', '240', '450000', '防城港', 'FCG', 'F', '108.354723', '21.686739', 'https://imgb.mofangx.com/test/c66d070c3fb848368649798d82dd16a7.jpg', '大平坡,怪石滩,万尾金滩,', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">防城港市是广西壮族自治区下辖的一个地级市，地处我国大陆海岸线的最西南端，亦作为广西北部湾经济区的核心城市之一，其是一座独具特色的港口城市、边关城市、海湾城市。<br>因西南与越南交界，是中国唯一与东盟各国陆海相连的城市，只需3个小时就能乘船到越南著名的下龙湾；而与越南芒街一河相隔，一桥相连的防城港市东兴口岸，更是从此直接步行就能出关到越南。<br>除此外，防城港也将海之特色彰显得淋漓尽致，江山半岛滩平沙软，白浪滩、怪石滩、月亮湾等都是盛夏时节，人气最旺的聚集地。在领略海之波澜的同时，还有以万尾金滩为代表的京族三岛集碧海、金滩、绿岛、京族风情于一身，民风淳朴，风光旖旎。此外，还有山的多姿，横亘中部的十万大山森林公园苍茫广袤，峰峦叠嶂，雄奇险秀，是户外运动爱好者的好去处。<br>白浪滩：旧称“大平坡”，是防城港境内的著名景点，拥有宽阔而广大的平坦沙滩，翻涌着白浪的海面，是玩海戏浪的佳处。<br>仙人山公园：最高海拔196米，是防城港市区的至高点。登上山顶极目远眺，防城港的美景尽收眼底。<br>海珍路市场：防城港市内最繁华的市场，餐饮、购物、娱乐集中一体。<br>兴港大道：防城港市内的中心大道，最繁华的街道。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">5-10月。&nbsp;<br>防城港市属南亚热带季风气候，受海洋和十万大山山脉的影响，常年阳光充足，雨量充沛，气候宜人，其实一年四季都适合旅游。但最佳还是在5-10月，既能到海边游玩，清凉解暑，还能吃到海鲜大餐。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p>穿衣指南</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">防城港年平均气温达22℃左右，在夏季来时要注意防晒。春秋季的清晨及傍晚海风吹拂，稍有凉爽，可带上薄长袖外套防寒。到了冬季，雨量稀少，成为全年最干燥寒冷的季节，偶尔会有霜冻，需要适时加衣。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">港口区包括渔洲坪、白沙万两个街道办事处和企沙、光坡、公车3镇；防城区包括防城、那梭、大菉、那良、峒中、华石6镇和茅岭、滩营、扶隆、江山4乡。在港口区企沙镇一带说防城话，在港口区公车、防城区附城、大菉、那良、那垌、滩散、垌中、华石、滩营、平旺、扶隆、那勤、江山13个乡镇的部分村落和防城、那梭、板八、茅岭4个乡镇的少数村落说的是客家话。在防城区那良、那垌、峒中、板八、滩营、平旺等山区乡镇的部分村落和边境沿线，以及沿海乡镇的少数村落说的是壮语。那垌、滩散、峒中、板八等乡镇的部分村落的瑶族使用勉语。京族使用京语。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p>历史</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">防城港地区历史悠久。先秦时期，这里属百越之地。秦为象郡辖地，汉为合浦郡辖地，南朝时为黄州辖地。光绪13年（1887年）划钦州西部设防城县，属广东省。建国前后属广东辖地。1951年划给广西管辖，1955年划归广东管辖。1965年又划为广西管辖，属钦州地区。1984年国务院批准防城港与北海市一起列为全国14个沿海开放城市之一。1985年3月，设立地市级建制的防城港区。1993年5月23日国务院批准设立地级防城港市。</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:09:54', '', '0', 'group1/M01/46/42/eBif6Fltv-OAJDRaAACyIAIz1qM594.jpg', 'group1/M01/46/44/eBif6FltwieAcy4CAAuPU4LzErs352.jpg,group1/M01/46/44/eBif6FltwiiAD8htAATwmAcBohU851.jpg');
INSERT INTO `sys_city` VALUES ('450700', '240', '450000', '钦州', 'QZ', 'Q', '108.654367', '21.980882', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">钦州市北邻广西首府南宁，位于广西壮族自治区南部，北部湾沿岸，东与北海市和玉林地区相连，西与防城港市毗邻。钦州濒临辽阔的钦州湾，海域面积为 908.37平方公里。海洋资源十分丰富。海域内阳光充足，水温适宜，浮游生物较多，适合各种鱼类和其它海产、飞禽鸟类的繁殖和生长。小岛屿303个，陆 地海岸线长520.8里，是有名的“海豚之乡”。钦州市位于南华准地台的南端，地质构造复杂，地层发育较全，出露地层以下古生界志留系最为发育；岩浆岩以酸性侵入岩为主，主要有花岗岩和流纹岩；褶皱、断裂构造发育，并具明显的分带性，存在发生中等以上地震的条件。[</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">四季皆宜</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">2天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">带海洋性的热带季风气候</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:44:44', '', '0', 'group1/M01/46/45/eBif6FltyZCABitaAAC-RfdiOuU568.jpg', 'group1/M01/46/45/eBif6Fltyk6ACKWPAARCOWD91Vk164.jpg,group1/M01/46/45/eBif6Fltyk-ARGmTAAIgjjmVK3E589.jpg,group1/M01/46/45/eBif6FltylGAIe9OAABUMJcJ2Is617.jpg,group1/M01/46/45/eBif6FltylOAbIiJAAE3DNcDQTQ834.jpg');
INSERT INTO `sys_city` VALUES ('450800', '240', '450000', '贵港', 'GG', 'G', '109.599048', '23.111718', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">贵港市是一座具有二千多年历史的古城，是新崛起的西江经济走廊中心的一颗明珠。特产有菠萝蜜、贵港莲藕、龙眼、桂圆干、覃塘毛尖茶、罗汉果等。贵港市因城里多种植荷花而别称荷城，贵港拥有众多景点和古迹，龙凤石林、龙岩洞、达开水库、安澜塔、新世纪广场、九凌湖、君子垌段心围、西山、龙潭森林公园、平天山等景区景点。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">3月中旬—10月底</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">1天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带季风气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">粤语、客家语、壮语等</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:15:03', '', '0', 'group1/M01/46/44/eBif6Fltw16ACqu_AADIQmbN2UM126.jpg', 'group1/M01/46/44/eBif6Fltw2iAX13EAAD26Jo-jgw230.jpg,group1/M01/46/44/eBif6Fltw2uARCyeAACYjViruck966.jpg,group1/M01/46/44/eBif6Fltw22AMkoAAAB3TFSW8Io725.jpg,group1/M01/46/44/eBif6Fltw3CAE9LnAAFbhynTw1M498.jpg');
INSERT INTO `sys_city` VALUES ('450900', '240', '450000', '玉林', 'YL', 'Y', '110.18122', '22.654032', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">玉林市是广西壮族自治区东南部所辖的一个地级市，是广西最大、全国著名的侨乡。玉林同时也是中国优秀旅游城市，拥有江南四大名楼之一的真武阁、四大名关之一的鬼门关，著名风景名胜有云天文化城、大容山森林公园、谢鲁山庄、都峤山等120多处。有“岭南美玉、胜景如林”的美誉。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">夏秋最佳</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">1天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带季风气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">以玉林话、客家语为主，少量闽方言、壮语</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:52:07', '', '0', 'group1/M01/46/45/eBif6Flty3yASSQGAADh6jUCBqM081.jpg', 'group1/M01/46/45/eBif6FltzAqAKwXWAADkGXwoNa4047.jpg,group1/M01/46/45/eBif6FltzAyAQkNiAAFPkaIpLR8780.jpg,group1/M01/46/45/eBif6FltzA6AYRB_AAF4FIiGOUo084.jpg,group1/M01/46/45/eBif6FltzBCAPWfWAAU3GjtQtYE913.jpg');
INSERT INTO `sys_city` VALUES ('451000', '240', '450000', '百色', 'BS', 'B', '106.618202', '23.90233', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">百色位于广西省西北部，拥有众多令人羡慕的旅游资源，世界上最大的天坑群景区乐业县大石围天坑群、中国单级落差最大的瀑布靖西通灵大峡谷瀑布、终年常绿山清水秀的布柳河热带雨林美景以及罕见的乐业县南国雪景、壮瑶苗等少数民族风情等等，这一切都使得百色独具魅力。百色也是著名的革命胜地。邓小平曾经在这里发动和领导了百色起义，在这片热土上留下许多宝贵的历史财富。现有百色起义纪念馆、粤东会馆、清风楼、岑氏古建筑群等人文景观。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">夏秋季节</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">1天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带季风气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">壮语、粤语-邕浔片（极少数）</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 15:48:59', '', '0', 'group1/M01/46/42/eBif6Fltu9CAOxTlAADhBLSqzi0673.jpg', 'group1/M01/46/42/eBif6FltvKmAMjcSAANA0dLRKoY890.jpg,group1/M01/46/42/eBif6FltvKuAb3i2AAMK2h9H_qE162.jpg,group1/M01/46/42/eBif6FltvK-AQv9yAAN7EEH5CnM729.jpg,group1/M01/46/42/eBif6FltvLSAKJ2KAAFO_hr8uR4330.jpg');
INSERT INTO `sys_city` VALUES ('451100', '240', '450000', '贺州', 'HZ', 'H', '111.566871', '24.403528', '', '', '0', '0', '0', '2', '0', '0', '1', '<p>贺州速览</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">贺州，位于广西东部，地处湘、粤、桂三省（区）结合部，历史上有“三省通衢”之称。其下辖八步区、平桂管理区、钟山县、富川瑶族自治县、昭平县等三县两区。<br>和广西很多城市一样，这里也有山有水、风景秀丽、民风淳朴，汇聚了少数民族，风情浓郁。每年农历十月十六日是这里有名的盘王节，数万瑶胞载歌载舞，庆丰年，颂盘王，吸引了不少游客前来。<br>早在明清之际，这里就是桂湘粤三省（区）的商品集散地，特殊的历史文化造就了贺州众多的人文景观、名胜古迹。境内有国家级、自治区级重点文物保护单位10多处。目前全国保存最完整的明清古建筑群——黄姚古镇就在这里。&nbsp;<br><strong>姚江：</strong>是贺州的母亲河。在黄姚古镇，更是传镇而过成S型，犹如一个八卦阵。<br><strong>八步区：</strong>是贺州市下辖的区之一，是贺州市委、市政府所在地，即为贺州市中心。一般说到八步的汽车即是到贺州。<br><strong>黄姚古镇：</strong>在贺州昭平县，为中国最具旅游价值古城镇之一，已有近千年历史。电影《面纱》及香港连戏剧《茶是故乡浓》等拍摄曾在此取景。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">4-5月，9-11月。可到姑婆山爬山赏花，到十八水、紫云洞避暑观瀑布，到黄姚古镇静静地放肆灵魂。</p><p>穿衣指南</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">贺州气候温和，年平均气候在18℃左右。4-5月还较微凉，需穿薄外套护体，尤其登山时昼夜温差大，要特别注意保暖；夏季清凉着装即可，蚊子狠毒，要记得带防蚊药水。冬季也不太冷，穿毛衣加外套即可。</p><p>消费水平</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">贺州消费不高，来此游玩以住宿及交通花费为主，一天游500元可基本满足。</p><p><br></p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:25:07', '', '105', 'group1/M01/46/44/eBif6FltxVuANEs6AADQTqKbe8I693.jpg', 'group1/M01/46/44/eBif6Fltxa6AaewrAB6vwpFezLM920.jpg,group1/M01/46/44/eBif6FltxbeAfJVbAC7MpBhQpSM128.jpg,group1/M01/46/44/eBif6Fltxa-Aap3vAAfWig0XIxQ951.jpg,group1/M01/46/44/eBif6FltxbiATok8AAHrBY4ghzs843.jpg');
INSERT INTO `sys_city` VALUES ('451200', '240', '450000', '河池', 'HC', 'H', '108.084961', '24.692795', 'https://imgb.mofangx.com/test/9dc36e66f9f04a43a1709f9b210b7c32.jpg', '巴马盘阳河,三门海,巴马长寿村,', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">河池位于广西壮族自治区，是一块旅游资源丰富的宝地。这里拥有令人叹为观止的喀斯特地貌景观、千姿百态的山水景观、民风淳朴的民族风情、举世罕见的长寿资源。大自然对河池厚爱至极，赐予她一方美丽山水，然而她仍“养在深闺人未识”，亟待开发。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">夏秋最佳</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">1天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">西南官话桂柳片，壮语</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带季风气候</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:22:09', '', '0', 'group1/M01/46/44/eBif6Fltw7mALgAGAACbe6ojTnE696.jpg', 'group1/M01/46/44/eBif6FltxMyAPt6CAAIVzg-_HPQ323.jpg,group1/M01/46/44/eBif6FltxM2AfvlWAAEQywyKAaY702.jpg,group1/M01/46/44/eBif6FltxM6AfNuWAAGcW2v2aR8774.jpg,group1/M01/46/44/eBif6FltxNCAAUzQAACUjPi15k4563.jpg');
INSERT INTO `sys_city` VALUES ('451300', '240', '450000', '来宾', 'LB', 'L', '109.221465', '23.750303', '', '', '0', '0', '0', '2', '0', '0', '1', '<p style=\"font-family: 微软雅黑;\">简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; font-family: 微软雅黑; padding: 0px; word-wrap: break-word;\">来宾市为广西壮族自治区管辖的地级市，位于居广西中部，故有“桂中”之称，又因资源物产丰富，有“广西煤都”、“中国糖都”、“世界瑶都”等美称。深居广西腹部，经济、交通地位十分重要，地缘优势十分明显。同时境内旅游资源丰富。有亚洲现存规模最大、保存最完整的土司建筑群，被喻为“壮乡故宫”的忻城县莫氏土司衙署；有着“中南第一温泉”美称的象州花池温泉，以及圣堂山，莲花山等。</p><p style=\"font-family: 微软雅黑;\">最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; font-family: 微软雅黑; padding: 0px; word-wrap: break-word;\">全年皆宜</p><p style=\"font-family: 微软雅黑;\">建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; font-family: 微软雅黑; padding: 0px; word-wrap: break-word;\">2天</p><p style=\"font-family: 微软雅黑;\">当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; font-family: 微软雅黑; padding: 0px; word-wrap: break-word;\">亚热带季风气候</p><p style=\"font-family: 微软雅黑;\">语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; font-family: 微软雅黑; padding: 0px; word-wrap: break-word;\">柳州话、壮语</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:32:45', '', '0', 'group1/M01/46/44/eBif6FltxheANhLkAACt3Y1_hn4129.jpg', 'group1/M01/46/44/eBif6Fltx3mAIEwhAAGx_0ubESQ025.jpg,group1/M01/46/44/eBif6Fltx3uAGssQAAGYYDSyU6U873.jpg,group1/M01/46/44/eBif6Fltx3yADllbAAEYIO_B-uo503.jpg,group1/M01/46/44/eBif6Fltx3-AP1seAAvmiok9sAU633.jpg');
INSERT INTO `sys_city` VALUES ('451400', '240', '450000', '崇左', 'CZ', 'C', '107.365094', '22.377253', 'https://imgb.mofangx.com/test/7c58e70f87604ecd8f77293619478b57.jpg', '德天瀑布,花山壁画,友谊关,', '0', '0', '0', '2', '0', '0', '1', '<p>简介</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">崇左市位于广西的西南部，背靠大西南，与越南接壤，处于华南经济圈、西南经济圈和东盟经济圈交汇的中心地带，是广西边境线陆路最长的地级市。自古以来，崇左就是壮族的聚居地，目前壮族人口占全市人口的80%左右。崇左市地质构造古老，地形地貌复杂，以喀斯特地貌为主，独特的地貌造成了崇左丰富的自然景观，旅游资源丰富。境内有中国最美的瀑布之一——德天跨国大瀑布，天然盆景崇左石林，世界八大斜塔之一的崇左归龙斜塔，中国九大名关之一的凭祥友谊关等。</p><p>最佳旅行时间</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">春秋适宜</p><p>建议游玩天数</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">2天</p><p>当地气候</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">亚热带湿润季风气候</p><p>语言</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; word-wrap: break-word;\">粤语、邕浔片、南宁话、壮语</p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 15:58:40', '', '100', 'group1/M01/46/42/eBif6FltvaSAMtB1AACaiCEGwvc405.jpg', 'group1/M01/46/42/eBif6Fltv3eAXn64AANA8ipliNs254.jpg,group1/M01/46/42/eBif6Fltv3mAJ1c3AAE9Rayu79o331.jpg,group1/M01/46/42/eBif6Fltv32AKJMBAAFZPpehMWw885.jpg,group1/M01/46/42/eBif6Fltv4KAe3O4AAFkmOplf8o629.jpg');
INSERT INTO `sys_city` VALUES ('460100', '240', '460000', '海口', 'HK', 'H', '110.198286', '20.044412', 'https://imgb.mofangx.com/test/2e37acd49149458a840a43d76e300838.jpg', '五公祠,假日海滩,万绿园,', '1', '0', '1', '2', '0', '0', '1', '<div id=\"intro_3488\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left-width: 2px; border-left-style: solid; border-left-color: rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, \'Lucida Grande\', \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Hiragino Sans GB W3\', SimSun, \'PingFang SC\', STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left-width: 2px; border-left-style: solid; border-left-color: rgb(255, 157, 0);\"><p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">简介<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;\r\nmso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">海口是海南省的省会，曾经入选中国特色魅力城市。虽然他没有三亚出名，但也因此没有三亚那般喧闹，海岛的浪漫情怀却不减分毫。海口绵长的海滩边是一望无际的琼州海峡，常有帆船出没。古老的火山群布满珊瑚般的熔岩流，将古老的时光凝固在这里，寂静而又舒适。湿湿的海风略凉，花香却清晰，偶尔还会有小鸟的歌声。找个生意兴旺的老爸茶店坐下，和热情的阿婆歇息搭讪。街边三三两两的小贩挑担叫卖，担子里几乎都是海南本地的特色水果，山竹、鸡蛋果、莲雾。最好是黄昏，一个人漫步骑楼老街，从解放西路到博爱路，从得胜沙到中山路。摩托车灯亮了，老建筑上的皱纹模糊了，攀着三角梅的阳台和窗子被灯光照出暖意。三轮车在拐角处发出叮叮咚咚的铃声，斑斓的小店亮出诱惑的光影，<span lang=\"EN-US\">CD</span>店的劣质音响轮番播放哀怨的<span lang=\"EN-US\">80</span>年代情歌。这就是安静舒适，复古怀旧的海口，让人魂牵梦绕的椰城。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">最佳旅行时间<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span lang=\"EN-US\" style=\"font-size:8.0pt;font-family:宋体;mso-ascii-theme-font:minor-fareast;\r\nmso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:\r\nminor-fareast;mso-bidi-font-family:Arial;mso-font-kerning:0pt\">10</span><span style=\"font-size:8.0pt;font-family:宋体;mso-ascii-theme-font:minor-fareast;\r\nmso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:\r\nminor-fareast;mso-bidi-font-family:Arial;mso-font-kerning:0pt\">月到翌年<span lang=\"EN-US\">5</span>月是海口旅游的最佳时节。这期间海口风平浪静，气候怡人。北方酷寒来袭时，这里却暖风和煦。此时，海南还有农历<span lang=\"EN-US\">“</span>三月三<span lang=\"EN-US\">”</span>期间的<span lang=\"EN-US\">“</span>椰子节<span lang=\"EN-US\">”</span>和农历二月初九至十九的<span lang=\"EN-US\">“</span>军坡节<span lang=\"EN-US\">”</span>。一个是融合以海南椰文化和黎苗<span lang=\"EN-US\">“</span>三月三<span lang=\"EN-US\">”</span>民俗为特色的国际盛会，一个是在海南相传<span lang=\"EN-US\">1300</span>多年的乡情民俗。<span lang=\"EN-US\"><br>\r\n</span>尽管海口全年的平均温度都在<span lang=\"EN-US\">20</span></span><span style=\"font-size:\r\n8.0pt;font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:\r\n宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:宋体;mso-font-kerning:0pt\">℃</span><span style=\"font-size:\r\n8.0pt;font-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:\r\n宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">上下，但每年<span lang=\"EN-US\">5</span>至<span lang=\"EN-US\">10</span>月是海口的雨季，尤其<span lang=\"EN-US\">9</span>月，是降雨高峰期，游客出行会有不便。此外，海南岛的东部被称为<span lang=\"EN-US\">“</span>台风走廊<span lang=\"EN-US\">”</span>，因此夏秋旅游别忘记收听气象部门发布的台风警报。遇上强台风侵袭，海口交通会受影响，可能航班会取消，水路会停运。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">穿衣指南<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;\r\nmso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">海口全年的平均温度都在<span lang=\"EN-US\">20</span></span><span style=\"font-size:8.0pt;font-family:宋体;mso-ascii-theme-font:minor-fareast;\r\nmso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:\r\nminor-fareast;mso-bidi-font-family:宋体;mso-font-kerning:0pt\">℃</span><span style=\"font-size:8.0pt;font-family:宋体;mso-ascii-theme-font:minor-fareast;\r\nmso-fareast-font-family:宋体;mso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:\r\nminor-fareast;mso-bidi-font-family:Arial;mso-font-kerning:0pt\">上下，即使在冬季，相对北方的天气，也是温暖舒适的，只需准备简单的外套和<span lang=\"EN-US\">T</span>恤即可。夏季多雨，请备好雨具。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">历史<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;\r\nmso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">海口是整个海岛最具历史文化魅力的城市，起源于汉代，开埠于宋末元初。<span lang=\"EN-US\">“</span>海口<span lang=\"EN-US\">”</span>一名最早出现于宋代，迄今已有九百多年的历史。<span lang=\"EN-US\"><br>\r\n</span>历史上曾设有宋代的海口浦，元代的海口港，明代的海口都、海口所、海口所城，清代的海口商埠、琼州口，民国初期的海口镇。海口曾地属广西、划归广东、隶属于琼山县。直到<span lang=\"EN-US\">1988</span>年<span lang=\"EN-US\">4</span>月<span lang=\"EN-US\">13</span>日，第七届全国人民代表大会通过关于设立海南省、建立海南经济特区的决议，海南建省，海口市成为海南省省会。<span lang=\"EN-US\"><br>\r\n</span>历史长河，载述海口市漫长曲折的发展历程，也记述海口市光荣的革命传统，更展现丰富的人文史迹景观。现有为纪念被贬谪来海南岛、传播文化推动海南文化发展和交流的名臣而建的<span lang=\"EN-US\">“</span>五公祠<span lang=\"EN-US\">”</span>，为纪念明代琼籍清官海瑞的海瑞墓园；还有中共琼崖<span lang=\"EN-US\">“</span>一大<span lang=\"EN-US\">”</span>旧址和李硕勋烈士纪念亭、金牛岭烈士陵园等革命名胜。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">语言<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;\r\nmso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">海南话属闽南语系<span lang=\"EN-US\">,</span>一般人带文昌口音。用普通话与当地人交流问题不大，只是很多人的地方口音特别重，听起来相当费劲。<span lang=\"EN-US\"><o:p></o:p></span></span></p></div></div>', '0', '2015-05-12 16:16:02', '', '2017-07-19 14:56:40', '', '0', 'group1/M01/46/44/eBif6FltxHOAKoKOAAE04oXHjgY622.jpg', 'group1/M01/46/44/eBif6FltxHuAJ7KOAAJY_vROBX0505.jpg,group1/M01/46/44/eBif6FltxH6AOIglAAQmbk06mWc250.jpg,group1/M01/46/44/eBif6FltxICARcLLAAg_jfAVUNo560.jpg,group1/M01/46/44/eBif6FltxIWACgaiAAGngoqYJog529.jpg,group1/M01/46/44/eBif6FltxIiAFsdpAAu5yZUYYlc376.jpg');
INSERT INTO `sys_city` VALUES ('460200', '240', '460000', '三亚', 'SY', 'S', '109.511692', '18.253073', 'https://imgb.mofangx.com/test/c4bc00a684434986a77f2da629e0e007.jpg', '天涯海角,三亚湾,蜈支洲岛,', '0', '0', '0', '2', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">三亚速览</div><div class=\"figure\" style=\"margin: 20px 0px; padding: 0px; word-wrap: break-word; font-size: 12px; color: rgb(153, 153, 153); line-height: 20px;\"><a href=\"http://n2-q.mafengwo.net/s9/M00/28/DD/wKgBs1gHQ3mARR_9AAkxZ4kAfGA27.jpeg\" target=\"_blank\" title=\"点击查看原图\" style=\"color: rgb(153, 153, 153); text-decoration: none; cursor: pointer;\"><img src=\"http://n2-q.mafengwo.net/s9/M00/28/DD/wKgBs1gHQ3mARR_9AAkxZ4kAfGA27.jpeg\" style=\"border: 0px; vertical-align: middle; margin-bottom: 8px; max-width: 100%;\"></a><div style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">图：三亚椰梦长廊。by CCChen</div></div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">三亚位于中国海南岛的最南端。狭长的城市坐落在绵延数百公里的海岸线上。绝美的海滨风光令三亚成为了中国最著名的海滨度假胜地，享有“东方夏威夷”的美誉。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">水清沙白的海滩，枝繁叶茂的雨林及物美价廉的海鲜是三亚的招牌。三亚湾、大东海、亚龙湾三大海滩是游客主要游览区，三亚湾紧邻市区，亚龙湾的水质最清。除此之外，三亚还覆盖有茂密的热带雨林，亚龙湾热带天堂森林公园是登高赏景的好地方，呀诺达热带雨林可以体验原始丛林中攀岩瀑布的惊险与刺激。来三亚，一顿丰盛的海鲜大餐也少不了。第一市场的海鲜物美价廉，上百种鲜活美味任你选。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">三亚还拥有两座绝美的离岛，基础设施完善的蜈支洲岛是潜水胜地，西岛则以更优惠的价格吸引着众多游客。喜欢少数民族风情的游客还可以走进三亚的黎族苗寨一览当地原住民的生活风貌。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">10-3月是三亚的最佳旅行时间。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">10-3月：</span>此时中国北方已经进入寒冬，而三亚却依旧是温暖如春。天气晴朗少雨，气温维持在25度左右，宜人的天气也迎来了三亚的旅游旺季。各大节庆、活动层出不穷。旺季期间的交通、住宿等费用都有一定程度的上调，整体的旅行开销也会增长。如果时间允许，尽量避开人满为患的春节假期出行。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">4-5月：</span><br>三亚的旅游淡季，温度回升至30度左右，天气晴朗炎热。当地的苗族与黎族会在农历三月三（公历4月初）举办大型的民族节庆活动。这两个月的酒店及交通费用相对较低，游客也较少。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">6-9月：</span><br>此时三亚正值雨季，但多为阵雨，持续时间不长，对出行影响较小。7、8月最为炎热，气温高达35度上下，酷暑暴晒，要注意防暑降温。9月偶遇台风，游客出行需留意天气变化。盛夏时节来三亚的旅行费用适中，虽然会迎来暑期的学生客流，但在烈日炎炎的下，很多有出行打算的游客都不会选择三亚。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">4-5天。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">三亚这样的海滨度假城市适合放慢脚步游玩。当地的景点并不集中，东西向分布在狭长的海岸线上。各景点之间的路程较远，一天玩1-2个景点为宜。留够4-5天的时间基本可以玩的全面，又不会觉得太仓促。想去蜈支洲、西岛这些离岛的话，最好腾出一整天的时间，晚上还可以回市区吃顿海鲜大餐。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">三亚由于是旅游热门城市，整体的消费水平几乎接近北京、上海等一线城市。三亚整体的旅行成本受季节性影响较大，旺季的酒店、包车价格通常会比淡季高出一倍。景点内的消费也较为昂贵。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">景点：</span>&nbsp;三亚的景点费用支出占总支出的比重较大。除海滩免费外，基本上大多数的景点都要收取门票，而且价格均超过百元。天涯海角景区门票为101元，亚龙湾热带天堂森林公园门票为175元。蜈支洲和西岛的一价全包套票要近千元。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">餐饮：</span>三亚的餐饮消费并不高，一碗抱罗粉8元左右，小店吃一顿正餐人均30元。在第一市场吃一次海鲜大餐人均也不过150元。但景区内的餐饮要相对贵很多，矿泉水10-15元一瓶，蜈支洲岛一碗牛肉面更是要贵到近50元一碗。如果白天能自备些水和零食，在景区内会省下不少钱。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">住宿：</span>三亚酒店的价格受季节性影响很大，同样的酒店在淡旺季价格会差出近一倍。三亚市区内不沿海的酒店价格相对便宜，一般在100-200元一晚。沿海的酒店中三亚湾的最便宜，一晚在300元左右，但距离市中心较远。奢侈的酒店除了市中心的凤凰岛外，几乎都位于亚龙湾和海棠湾，一晚价格在600至上千元不等。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">交通：</span>三亚的公共交通主要依靠公交车及旅游专线，大多数游客都会选择包车或打车往返各景点间。从市区至大东海打车约12元，至亚龙湾或天涯海角至少需75元，至蜈支洲码头至少需要90元。包车的价格淡季一天在200-300元，旺季在300-400元。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-14 14:35:05', '', '133', 'group1/M01/46/3A/eBif6FloYnuAG4G9AADJmFvAqL4520.jpg', 'group1/M01/46/3A/eBif6FloZfeAUiJ-AAB1XI492hk009.jpg,group1/M01/46/3A/eBif6FloZfyAOMT2AAJCPKh3sjM878.jpg,group1/M01/46/3A/eBif6FloZf6AUxgHAAGSjsk2mMw789.jpg,group1/M01/46/3A/eBif6FloZgaAIG0SAAKUo122WIo501.jpg,group1/M01/46/3A/eBif6FloZhCACcM7AAtXou6I88I183.jpg,group1/M01/46/3A/eBif6FloZhGAUKLPAAIDDePDMHQ539.jpg,group1/M01/46/3A/eBif6FloZhGAMKxbAADka8LNv4o034.jpg,group1/M01/46/3A/eBif6FloZhOABCFwAAHj4IJmQ7E180.jpg,group1/M01/46/3A/eBif6FloZhaAX_LLAAJ7d7755wM118.jpg');
INSERT INTO `sys_city` VALUES ('460300', '240', '460000', '三沙市', 'SSS', 'S', '112.338649', '16.831004', 'https://imgb.mofangx.com/test/e828216f1cbf4f25a95d867df55ef805.jpg', '全富岛,鸭公岛,永乐群岛,', '0', '0', '0', '2', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left-width: 2px; border-left-style: solid; border-left-color: rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, \'Lucida Grande\', \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Hiragino Sans GB W3\', SimSun, \'PingFang SC\', STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left-width: 2px; border-left-style: solid; border-left-color: rgb(255, 157, 0);\"><p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">简介<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;\r\nmso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">三沙市地处中国南海，是中国领土最南端、地理位置最南、总面积最大（含海域）、陆地面积最小和人口最少的县市，地貌景观全部由珊瑚礁地貌构成，这里景色极美海水极清，近海的地方能从水面看见水下五彩缤纷的珊瑚。西沙群岛是三沙市位置最北的群岛，主体部分还分为永乐群岛与宣德群岛。\r\n中沙群岛位于西沙群岛东南部，多为环礁、暗沙。 南沙群岛是三沙市位置最南、分布最广的群岛。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii