INSERT INTO `sys_city` VALUES ('469002', '240', '460000', '琼海', 'QH', 'Q', '110.474581', '19.25914', '', '', '0', '0', '0', '2', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">琼海位于海南岛的东部，是一个清新美丽、文化气息浓厚的城市。来到琼海，美丽的自然景观绝对是一大看点，这里有1308级贴崖登山石阶的白石岭，可登至峰顶观”白石岭八景“，更有滋养琼海肥沃土地的万泉河，浩浩荡荡奔到博鳌流入南海，在万泉河出海口更是集三河、三岛、两港、一石于一地，有海水、沙滩、红礁、林带，是世界河流出海口自然风光保护地区之一。除此之外，当地的文化氛围也十分浓厚，充满传奇的红色娘子军纪念公园是省级重点革命纪念建筑物保护单位，再现了娘子军当年的辉煌，清新美丽的莲花馆填补了国内以莲花为主题，拥有全面介绍莲花的科技、应用、历史等荷花文化专门展览馆的空缺，殊惊世界的博鳌亚洲论坛向世人展现了中国的魅力。红色的历程、蓝色的诱惑、绿色的魅力，这一切都构架起琼海的与众不同，张扬出琼海的光芒四射。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">11月-4月最佳</p></div><div id=\"intro_unstrt-trav