INSERT INTO `sys_city` VALUES ('513400', '240', '510000', '凉山', 'LS', 'L', '102.267712', '27.88157', '', '', '0', '0', '0', '6', '0', '0', '1', '<section class=\"assistant\" style=\"font-size: 13.3333px; margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"font-size: 13.3333px; display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"text-align: justify; color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; line-height: 1.5em; margin-top: 5px; margin-bottom: 5px;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; line-height: 20px; widows: 1;\"><span style=\"line-height: 20px;\">凉山彝族自治州位于四川省西南部，东南与云南省隔金沙江相望，州府西昌市位于成昆铁路中段，自古以来就是通往云南和东南亚的“南方丝绸之路”的重镇。西昌还素有“月城”之雅称，西昌卫星发射中心所在地，是闻名中外的航天城。境内有汉、彝、藏、蒙古、纳西等10多个世居民族，是我国最大的彝族聚居区。这里冬无严寒，夏无酷暑，四季如春，享有“万紫千红花不谢，冬暖夏凉四时春”之誉。境内具有浓郁的民族特点和地区特点、民俗风情古朴而丰富多彩，如，独具特色的彝族民风、民俗和泸沽湖畔摩梭人的\"走婚\"习俗。旅游资源丰富，有泸沽湖、螺髻山、西昌古城、邛海大风顶自然保护区、雷波马湖风景区、会理古城等旅游景点。</span><br></span></p><section class=\"assistant\" style=\"font-size: 13.3333px; margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"font-size: 13.3333px; display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"widows: 1; font-size: 14px; text-indent: 2em; line-height: 1.5em; margin-top: 5px; margin-bottom: 5px;\"><span style=\"color:#666666;font-family:Arial, Lucida Grande, Microsoft Yahei, Hiragino Sans GB, Hiragino Sans GB W3, SimSun, PingFang SC, STHeiti\"><span style=\"line-height: 20px;\">四季适宜</span></span></p><section class=\"assistant\" style=\"font-size: 13.3333px; margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"font-size: 13.3333px; display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">建议游玩天数</span></p></section><p style=\"text-indent: 2em; line-height: 1.5em; margin-top: 5px; margin-bottom: 5px;\"><span style=\"color: #7F7F7F; font-family: 微软雅黑; font-size: 14px;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; line-height: 20px; widows: 1;\">4天<br></span></span></p><section class=\"assistant\" style=\"font-size: 13.3333px; margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"font-size: 13.3333px; display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">当地气候</span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; line-height: 1.5em; margin-top: 5px; margin-bottom: 5px;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; line-height: 20px; widows: 1;\"><span style=\"line-height: 20px;\">亚热带季风</span>气候<br></span></p><section class=\"assistant\" style=\"font-size: 13.3333px; margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"font-size: 13.3333px; display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">语言</span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; line-height: 1.5em; margin-top: 5px; margin-bottom: 5px;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; line-height: 20px; widows: 1;\"></span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; line-height: 20px; widows: 1;\">四川官话、彝语</span></p><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; line-height: 1.5em; margin-top: 5px; margin-bottom: 5px;\"><br></p>', '0', '2015-05-12 16:16:02', '', '2017-04-20 18:08:54', '', '0', 'group1/M01/40/C4/eBif6Fj4hoSAQKyjAACV_ColA3k580.jpg', 'group1/M01/40/C4/eBif6Fj4hxGAGsnwAAYn1AT6maA661.jpg,group1/M01/40/C4/eBif6Fj4hxOAVrdAAAF2_UJnS6Y129.jpg,group1/M01/40/C4/eBif6Fj4hxaAE3bfAAHRIYrLsZU632.jpg,group1/M01/40/C4/eBif6Fj4hxmALgq5AANyLL4ZYbM342.jpg');
INSERT INTO `sys_city` VALUES ('520100', '240', '520000', '贵阳', 'GY', 'G', '106.630153', '26.647661', 'https://imgb.mofangx.com/test/c308c8ab3b9940bf933c361eeb9737f0.jpg', '贵阳红枫湖景区,花溪风景区,甲秀楼,', '1', '0', '1', '6', '0', '0', '1', '<p><strong>贵阳速览</strong></p><p><a href=\"http://b4-q.mafengwo.net/s8/M00/07/F9/wKgBpVWnKHeAb6ucAAitrixHzIo58.jpeg?imageslim\" target=\"_blank\" title=\"点击查看原图\" style=\"color: rgb(153, 153, 153); cursor: pointer;\" _href=\"http://b4-q.mafengwo.net/s8/M00/07/F9/wKgBpVWnKHeAb6ucAAitrixHzIo58.jpeg?imageslim\"><img src=\"http://b4-q.mafengwo.net/s8/M00/07/F9/wKgBpVWnKHeAb6ucAAitrixHzIo58.jpeg?imageslim\" style=\"border: 0px; vertical-align: middle; margin-bottom: 8px;\" _src=\"http://b4-q.mafengwo.net/s8/M00/07/F9/wKgBpVWnKHeAb6ucAAitrixHzIo58.jpeg?imageslim\"></a></p><p>贵阳是贵州省的省会，地处贵州中部，四周山峦环绕，是名副其实的山城。对于大多数旅者来说，贵阳往往只是旅途中的一座中转站。虽然少有人认真地了解她，但依旧有黔灵山、青岩古镇等景点值得游览，有夜市能让你大饱口福。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\">盛夏的贵阳是避暑的极佳之处。这里气候宜人，最热时期也鲜有30℃以上的高温。绿林与城市的完美结合，不仅让整个城市翠意连绵，也营造出一个天然氧吧。所以，贵阳官方也打出“爽爽的贵阳”这句城市宣传语。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\">不过，“爽爽”道出的不仅是其夏日的凉爽，更有贵阳美食的美味爽口。一周不重样的早餐，丝娃娃、恋爱豆腐果、洋芋粑粑等街头小吃，清水烫、烤肉、烤鱼等夜市美食，酸汤鱼、辣子鸡等特色菜，想要尝遍，恐怕还真得待上一阵子。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\">除却青山秀水和美食，惬意自在是贵阳的另一张名片。这里的生活节奏舒缓，没有大都市的紧张与忙碌，年长的喜欢到公园路边打牌消遣，工作族也能时常去夜市吃得热火朝天。这慵懒闲散的大度和包容，可能是这座城市的另一种姿态。</p><p><strong>最佳旅行时间</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><span style=\"color: rgb(12, 12, 12);\">夏季是贵阳的最佳旅行季。</span></p><h5 style=\"font-size: 14px; line-height: 1.6em;\">夏季（6-8月）</h5><p style=\"margin-top: 15px; margin-bottom: 15px;\">贵阳的夏季舒适宜人，气温很少超过30℃，是当之无愧的避暑胜地。整个城市绿意盎然，郊区的峡谷漂流惊险又刺激，还有冰浆等夏日甜品。不过恰逢雨季，需随身携带雨具。</p><h5 style=\"font-size: 14px; line-height: 1.6em;\">春季（3-5月）</h5><p style=\"margin-top: 15px; margin-bottom: 15px;\">贵阳市区及周边百花齐放，大片的油菜花、梨花给你展现“金海与雪山”的盛景。各处樱花也在此间相继绽放，其景色或许不输武汉。</p><h5 style=\"font-size: 14px; line-height: 1.6em;\">秋天（9-11月）</h5><p style=\"margin-top: 15px; margin-bottom: 15px;\">秋季适合登黔灵山，除了登高望远，还可享受逗猴的乐趣。若想寻找秋意，红枫湖的火红枫林、花溪的黄金大道和芦苇都是典型的秋季美景。</p><h5 style=\"font-size: 14px; line-height: 1.6em;\">冬天（12-2月）</h5><p style=\"margin-top: 15px; margin-bottom: 15px;\">贵阳的冬季景色相比其他三季略显乏味，但好在丰富的美食从不缺席，酸辣的口味还可以帮你缓解湿冷的寒意。另外，泡温泉也是更适合冬季的消遣方式。</p><p><strong>消费水平</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">贵阳消费水平中等，比北上广等一线城市及旅游城市要低一些。</p><p><strong>景点</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">多数公园的基础门票不超过10元或免费开放，但内部可能会有景点、交通项目需另外收费。青岩古镇联票80元/人。</p><p><strong>餐饮</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">各类小吃价格较为实惠，粉面、丝娃娃、甜品等小吃一般10元左右/份，豆腐圆子1.5元/个。黔菜正餐人均一般在50-70元。</p><p><strong>交通</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">贵阳出行基本靠公交车，票价在1-2元/次。出租车的10元基本能到市区的各个地方。</p><p><strong>住宿</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px;\">贵阳市内各区域的酒店价格差异不大，三星酒店约200元/晚，四星约400元/晚，观山湖周边的五星酒店约750元/晚，青旅床位约50元/晚。</p><p></p>', '0', '2015-05-12 16:16:02', '', '2017-07-17 11:08:44', '', '114', 'group1/M01/46/3D/eBif6FlsI3KAQsSWAAHVxMzvJZc173.jpg', 'group1/M01/46/3D/eBif6FlsJoOAaATcAAJxd4PTUYc052.jpg,group1/M01/46/3D/eBif6FlsJoaAeGptAAGmIScQ7tc128.jpg,group1/M01/46/3D/eBif6FlsJoeAdfxKAAGiAuPVKBM242.jpg,group1/M01/46/3D/eBif6FlsJoqAe_GbAAJB-QW5KP4278.jpg');
INSERT INTO `sys_city` VALUES ('520200', '240', '520000', '六盘水', 'LPS', 'L', '104.830333', '26.59254', 'https://imgb.mofangx.com/test/142b03e699eb440097bcc4a3a768feb5.jpg', '盘县古银杏风景名胜区,??江,玉舍森林公园,', '0', '0', '0', '6', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left-width: 2px; border-left-style: solid; border-left-color: rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, \'Lucida Grande\', \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Hiragino Sans GB W3\', SimSun, \'PingFang SC\', STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left-width: 2px; border-left-style: solid; border-left-color: rgb(255, 157, 0);\"><p class=\"MsoNormal\" align=\"left\" style=\"line-height: 16.15pt;\"><span style=\"mso-bidi-font-size:10.5pt;font-family:\r\n宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;mso-fareast-theme-font:\r\nminor-fareast;mso-hansi-theme-font:minor-fareast;mso-bidi-font-family:Arial;\r\nmso-font-kerning:0pt\">简介<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"margin-top: 8.65pt; line-height: 11.5pt;\"><span style=\"font-size:8.0pt;\r\nfont-family:宋体;mso-ascii-theme-font:minor-fareast;mso-fareast-font-family:宋体;\r\nmso-fareast-theme-font:minor-fareast;mso-hansi-theme-font:minor-fareast;\r\nmso-bidi-font-family:Arial;mso-font-kerning:0pt\">六盘水位于贵州省西部、云贵高原一、二级台地斜坡上，是贵州省第三大城市，国家西电东送的主要基地，国家西部大开发南、贵、昆经济带的重要结点城市。六盘水地处华南、西南铁路大通道交汇点，有<span lang=\"EN-US\">“</span>四省立交<span lang=\"EN-US\">”</span>的美誉。六盘水资源丰富，风光秀美，境内有玉舍国家森林公园、玉舍滑雪场、乌蒙山国家地质公园、明湖国家湿地公园、韭菜坪景区、月亮河夜郎布依文化生态园等多处著名景点，六盘山因风光秀美，气候宜人，连续多次入选国家十大避暑旅游城市榜。<span lang=\"EN-US\"><o:p></o:p></span></span></p>\r\n\r\n<p class=\"MsoNormal\" align=\"left\" style=\"