INSERT INTO `sys_city` VALUES ('522300', '240', '520000', '黔西南', 'QXN', 'Q', '104.902892', '25.088607', 'https://imgb.mofangx.com/test/099492f99b0d4b14918dfdfe63831b82.jpg', '马岭河峡谷,万峰湖,贵州黔西南双乳峰景区,', '0', '0', '0', '6', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">黔西南州，是中国贵州省下辖的一个自治州，有着浓郁的少数民族风情，境内山川秀美、景色迷人，旅游资源丰富，有国家级风景区马岭河峡谷、万峰林、万峰湖，还有万峰林、仙鹤坪、贞丰三岔河、安龙招堤、兴义云湖山等奇特的自然风光，是世界锥状喀斯特地质地貌的典型代表。黔西南州历史悠久，古迹颇多，有“贵州龙”化石群、“兴义人”古人类文化遗址、夜郎文化遗址、南明历史遗迹“十八先生墓”、“永历皇宫”和抗战公路遗迹“晴隆二十四道拐”、何应钦故居等历史文化。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，景色各不相同</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">2天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">亚热带季风湿润气候</p></div><div id=\"intro_unstrt-travellanguage\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">西南官话</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-17 11:50:35', '', '0', 'group1/M01/46/3E/eBif6FlsNAuAN1CAAAD2mkcLh_8043.jpg', 'group1/M01/46/3E/eBif6FlsM8GAYSkrAAJyNnakX_M024.jpg,group1/M01/46/3E/eBif6FlsM8OAK3sHAADW_MM-utc284.jpg,group1/M01/46/3E/eBif6FlsM8aAJnhqAAJmYgBHHks556.jpg,group1/M01/46/3E/eBif6FlsM8qAC6JpAAHmkJhTZC0952.jpg,group1/M01/46/3E/eBif6FlsM82ABqsNAAG_l67hINY518.jpg,group1/M01/46/3E/eBif6FlsM9CAJfCsAAI8R0QdVag447.jpg,group1/M01/46/3E/eBif6FlsM9OAO4ePAAKV8l-Mvv8488.jpg,group1/M01/46/3E/eBif6FlsM9eAH_68AAFMrk-ZQ68128.jpg,group1/M01/46/3E/eBif6FlsM9uAQ_lDAAGg4oazUpo026.jpg');
INSERT INTO `sys_city` VALUES ('522600', '240', '520000', '黔东南', 'QDN', 'Q', '107.98026', '26.58338', 'https://imgb.mofangx.com/test/5023f63d39c440c89e499ce208159d06.jpg', '镇远舞阳河风景区,西江千户苗寨,青龙洞古建筑群,', '0', '0', '0', '6', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">黔东南速览</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">黔东南苗族侗族自治州位于贵州省东南部，地处云贵高原东南边缘，东邻湖南，南接广西，境内居住着苗、侗、汉、布依、水等20多个民族，民族风情非常浓郁。这里有世界上最大的苗寨和最大的侗寨，有独特的吊脚楼、风雨桥、鼓楼，这里有如天籁般的侗族大歌，有让人眼花缭乱的刺绣、银饰、蜡染……这里有着自成一派的文化体系，人们平静地生活，与世无争。黔东南因少数民族众多，从而拥有上百个民族节日，有“大节三六九，小节天天有”的说法，著名的民族节日有苗族的芦笙会、苗年、姊妹节，侗族的三月三歌会等，丰富多彩，各具特色。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><strong>凯里市：</strong>黔东南州州府，去往西江千户苗寨的必经之地，也是去往从江、榕江的重要交通中转。<br><strong>从江县：</strong>是重要交通中转，去往岜沙、小黄都从此转车。<br><strong>黎平县：</strong>黎平机场所在地，有飞往贵阳、广州的航班。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">秋季是到黔东南旅行的最好时间，雨水不会像夏季般频繁，气温不会像冬季时寒冷，温度适宜。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">黔东南的天气少有极端气温，冬天最低也不会低于0℃，室内没有暖气，室外会比室内暖和；夏天一般不会超过30℃，适合避暑。黔东南多雨，在5-10月间，有“天无三日晴”之说，总体上较为湿润。所以来黔东南旅行，雨具必不可少，夏季也要带一件外套，冬天最好备上一件羽绒服。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">黔东南经济水平不高，农家乐住宿、吃饭都为20-30元/人的标准，需要交门票的也仅是几个著名的村寨。总的来说，游玩1周，除开往返交通，花费在1000-2000元之间。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-17 11:06:11', '', '0', 'group1/M01/46/3D/eBif6FlsJjmAYfxCAADWs5yXuQA143.jpg', 'group1/M01/46/3D/eBif6FlsKYmAPATsAAMcjyszL2I851.jpg,group1/M01/46/3D/eBif6FlsKY6AcCNAAAOb1b0xo3U918.jpg,group1/M01/46/3D/eBif6FlsKZGAMa6sAAGXUb9G2GQ362.jpg,group1/M01/46/3D/eBif6FlsKZOAeX7wAAINa-g6Vzc341.jpg,group1/M01/46/3D/eBif6FlsKZeASMBgAAHG5REpk9w801.jpg,group1/M01/46/3D/eBif6FlsKZmAOXkzAAE1SKirSes210.jpg,group1/M01/46/3D/eBif6FlsKZuAERHEAAEIEusZzjw237.jpg,group1/M01/46/3D/eBif6FlsKZ2AeLqjAAHR1gUCsjw138.jpg,group1/M01/46/3D/eBif6FlsKaGAcxW8AAIMg8-4_As009.jpg');
