INSERT INTO `sys_city` VALUES ('620100', '240', '620000', '兰州', 'LZ', 'L', '103.834244', '36.06081', 'https://imgb.mofangx.com/test/32f863bb172840c38c2178fd96068dd4.jpg', '中山桥,五泉山公园,白塔山公园,', '1', '0', '1', '5', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">兰州速览</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">作为甘肃省省会、西北地区的中心城市，兰州的交通地位十分重要：北达敦煌，南抵甘南，西至西宁，东入银川，可以说是到西部旅游的必经之地。黄河水奔腾不绝，自西向东流过兰州，将其夹于南北两山之间，使之成为一个东西向延伸的狭长型城市。就旅游景点来看，最突出的要属黄河沿岸的“百里黄河风情线”了：中山桥、水车园、黄河母亲雕像，以及夜晚的白塔山夜景，都是来到兰州不能错过的景致。日日夜夜、经流不息流向远方的黄河水，使兰州成为一座“有性子”的城市，那滚滚浊浪和阵阵涛声，不仅养育了兰州一方水土，也造就了兰州人豪爽、粗犷的性格，这座城市也因为拥有黄河而愈发地温暖、丰盛。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">西关什字：西关什字是兰州重要的交通枢纽，在这里可以乘公交车到达兰州市区的各个地方。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">张掖路：张掖路是兰州最繁华的地带，也是兰州最老的商业街，购物、美食一应俱全。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">正宁路：正宁路夜市是兰州最有名的小吃街，以牛奶鸡蛋醪糟最为有名，各类羊肉、小吃相当丰富。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">南滨河路：“黄河风情线”的重点路段，有黄河母亲雕像、中山桥、水车公园等标志性景点。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">兰州是著名的“瓜果之乡”，出游兰州，以秋季为最佳，届时水果丰收，足以使人大饱口福。此外，九月的开斋节颇具特色，一年一度的“兰州中国丝绸之路节”也是在九月举办，会有众多游客聚集兰州，热闹非凡。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">兰州全年平均气温9℃左右，气候干燥，昼夜温差较大，春夏短暂、秋冬漫长。春秋出游需穿外套，夏季短袖即可，冬天要穿羽绒服。初到兰州，可能会觉得天气比较干燥，女生要带唇膏和保湿霜。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">兰州作为西北旅游线上一座中转城市，停留1-2天即可，住宿、餐饮共计500元左右。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-24 14:39:48', '', '0', 'group1/M01/41/CD/eBif6Fj9m_WAcrJ8AAODddf7kZU497.jpg', 'group1/M01/41/CD/eBif6Fj9nYeASsmjAAGAehwEhk0015.jpg,group1/M01/41/CD/eBif6Fj9nYyATteJAAeGZuIudp4712.jpg,group1/M01/41/CD/eBif6Fj9nZKAXQw4AAGBE94ux3o674.jpg,group1/M01/41/CD/eBif6Fj9nZaAYvTeAAHHbxpp5P8130.jpg');
INSERT INTO `sys_city` VALUES ('620200', '240', '620000', '嘉峪关', 'JYG', 'J', '98.288623', '39.773231', 'https://imgb.mofangx.com/test/5a33b52c443f43e9adfa7a8fca305625.jpg', '嘉峪关城楼,七一冰川,天下*墩,', '0', '0', '0', '5', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">嘉峪关速览</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">这里是古丝绸之路的交通要道，张骞曾带着满载丝绸的骆驼商队，缓缓西行；这里是明时万里长城的西起点，古时出了嘉峪关，就是茫茫戈壁，告别了中原故土，进入蛮荒之地。如今的嘉峪关，这个因酒钢而发展起来的移民城市里，聚集了来自东北、四川、河南、甘肃等地的人们，给这座新城带来了无限活力。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">嘉峪关最佳旅行时间为5-10月。<br>春季（3-5月）这里的绿比其他的地方来得更晚些，只是天空澄澈透明；<br>夏季（6-8月）此时气候凉爽，旅游节期间还有门票优惠；<br>秋季（9-11月）嘉峪关的东湖公园笼罩在色彩斑斓的秋色中，是赏关城日出日落，凭吊古意两相宜。<br>冬季（12-2月）嘉峪关白雪皑皑，异常寒冷，最好还是不要出行为好。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">嘉峪关早晚温差大，无论哪个季节到此，都请多备一件符合当季的外套。夏季紫外线强，备好防晒霜和太阳镜；春秋季节风沙大，请备好防风沙装备。若是冬季到此，防寒保暖是第一要务。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">嘉峪关的主要消费在于城内交通费用，若是自助游，游览关城、悬臂长城和长城第一墩三个主要景点，费用在120元，餐饮消费约在20-50元/人/餐之间。住宿费用在150元左右。一趟玩下来，除去大交通，人均需500-800元。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-24 11:33:15', '', '66', 'group1/M01/41/CC/eBif6Fj9cHqADPC9AAH1Ukxjhz4620.jpg', 'group1/M01/41/CC/eBif6Fj9cc-ARt7hAAFoxEoYkzk779.jpg,group1/M01/41/CC/eBif6Fj9cdKAQSvgAAEXgO4nmm8793.jpg,group1/M01/41/CC/eBif6Fj9cdWAM1tRAAa2Tq2gosQ055.jpg,group1/M01/41/CC/eBif6Fj9cdqAEyrfAAIDXoR3YEk938.jpg,group1/M01/41/CC/eBif6Fj9cdyAFLOwAAMEyibn3k0933.jpg');
INSERT INTO `sys_city` VALUES ('620300', '240', '620000', '金昌', 'JC', 'J', '102.187965', '38.521346', 'https://imgb.mofangx.com/test/d32254ecf14d467490b1a070a5ad88d4.jpg', '天井湖景区,铜陵凤凰山,', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">金昌市位于河西走廊东部，祁连山北麓，东连很多武威，西邻金张掖，南托古长城，与肃南裕固族自治县和青海省门源西族自治县毗邻，北靠戈壁滩，同内蒙古自治区阿拉善右旗接壤。因盛产镍闻名于世，被誉为“中国的镍都”。金昌有色金属矿储量比较丰富，特别是以镍为主的铂族贵金属矿藏得天独厚。金昌旅游资源丰富，境内文物古迹较多，有西汉时期骊遗址，汉、明长成及数众多的汉墓群；有唐代对容寺塔；有被誉 “河西中天一柱”的明代永昌钟鼓楼。还有巴丹吉林沙漠、祁连河沟自然生态风景区、皇城军马场等自然景观。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">5月—10月</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">2天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">大陆性温带干旱气候</p></div><div id=\"intro_unstrt-travellanguage\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">永昌话</p><div><br></div></div><div class=\"user-view\" style=\"margin: 20px 0px; padding: 0px; word-wrap: break-word; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 12px;\"></div>', '0', '2015-05-12 16:16:02', '', '2017-04-24 11:41:19', '', '0', 'group1/M01/41/CC/eBif6Fj9cjmAPkimAAHK5GZUgz8783.jpg', 'group1/M01/41/CD/eBif6Fj9c8GAG4OJAAEqkLetLQQ642.jpg,group1/M01/41/CD/eBif6Fj9c8SAAWIzAAGOMFc87FE494.jpg,group1/M01/41/CD/eBif6Fj9c8aAaPuzAAFC2TRjpy0565.jpg,group1/M01/41/CD/eBif6Fj9c8mAH5bCAAFNUOr4mxo219.jpg');
INSERT INTO `sys_city` VALUES ('620400', '240', '620000', '白银', 'BY', 'B', '104.138886', '36.545172', '', '', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">白银市是甘肃省下辖的一个地级市，位于甘肃省中部，黄河上游中段南邻兰州，北接宁夏、内蒙古，西经武威直通新疆，东连陇东。白银市矿产资源丰富，种类众多。明朝洪武年间，官方曾在此设立办矿机构“白银厂”，白银市缘此而得名。如今白银市是全国最大的有色金属工业基地和甘肃省重要的能源化工基地。境内有半山文化遗址、吊沟古城汉墓、景泰龟城、明代长城、钟鼓楼、法泉寺石窟等历史文化遗址，黄河石林、哈思山森林风景旅游区等自然景观，白银市还具有光荣的革命传统。境内有1936年10月，红军第一、二、四方面军胜利会师会宁县。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">春秋适宜</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px s