INSERT INTO `sys_city` VALUES ('621000', '240', '620000', '庆阳', 'QY', 'Q', '107.643632', '35.709077', 'https://imgb.mofangx.com/test/e0be30af1b584312975be897b23d06a9.jpg', '南梁革命纪念馆,北石窟寺,周祖陵森林公园,', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">庆阳市位于甘肃省东部，陕甘宁三省区的交会处，素有“陇东粮仓”之称。庆阳有璀璨的古代遗珍瑰宝。世界瞩目的“环江翼龙”和“黄河古象”化石，均发掘于境内马莲河流域。庆阳还是著名的红色革命圣地，有不少革命遗址。除了周祖陵森林公园、华夏公刘第一庙、北石窟寺、南梁革命政府旧址等著名景点，庆阳还有着文化底蕴深厚的民俗民情，刺绣、剪纸、皮影、民歌堪称\"四绝\"。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">四季皆宜，春夏最佳。</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word;