INSERT INTO `sys_city` VALUES ('622900', '240', '620000', '临夏', 'LX', 'L', '103.2426', '35.604536', '', '', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">临夏回族自治州，简称临夏州，是中国甘肃省下辖的一个自治州，位于甘肃省中部，黄河上游，黄土高原与青藏高原交界。地处省会兰州以南，西为青海省边界，南面是甘南藏族自治州，东邻定西市。首府临夏市是古代丝绸之路、唐蕃古道的重镇，明代四大茶马司之一，有“河湟雄镇”之称。临夏州民族文化和民俗风情独特，旅游资源众多。境内有以世界非物质文化遗产“花儿”和国家级非物质遗产砖雕为主的民族民俗文化，以中国“小麦加”·伊斯兰经典为代表的宗教历史文化。有炳灵寺石窟，莲花山，松鸣岩，刘家峡水库等旅游景点。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">春秋适宜</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">1天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">温带大陆性气候</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-24 14:55:18', '', '0', 'group1/M01/41/CD/eBif6Fj9nlmAAbybAAHKoL3RJgg256.jpg', 'group1/M01/41/CD/eBif6Fj9oTKAUJGAAAK3Sxf7TJc005.jpg,group1/M01/41/CD/eBif6Fj9oTaAY_39AAXxizXaQO8996.jpg,group1/M01/41/CD/eBif6Fj9oTiAaaBSAAXY2VNz3-s272.jpg,group1/M01/41/CD/eBif6Fj9oTuAL_1YAAJb2Q8G-eY882.jpg');
INSERT INTO `sys_city` VALUES ('623000', '240', '620000', '甘南', 'GN', 'G', '102.91166', '34.983274', 'https://imgb.mofangx.com/test/28b1882a9daf4525928526fa2575a7a3.jpg', '郎木寺,天葬台,碌曲尕海,', '0', '0', '0', '5', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">甘南速览</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">甘南藏族自治州位于甘肃省西南部，与青海、四川毗邻，属于安多藏区，首府为合作市。作为丝绸之路和唐蕃古道的重要组成部分，甘南州不仅有着广阔美丽的高原风光，还拥有着浓郁的藏传佛教文化风情，可以说景色和人文两者兼具。在圣境天堂般的甘南，草原上弥散着沁入心脾的酥油香，拉卜楞寺的辩经声随着炊烟一同升起，郎木寺如世外桃源般遗世独立，白龙江源头传来久远的召唤。转经廊外默念着六字真言、充满信仰的人们和匍匐在路上、磕着长头的老者都给我们带来心灵的震撼。<br>合作：为甘南藏族自治州首府，有多班车直达兰州、夏河拉卜楞寺、碌曲郎木寺。<br>夏河：在甘南旅游占据很重要的地位，著名的拉卜楞寺所在地，绝大多数去甘南的游客都会选择在夏河中转。<br>拉卜楞寺：拉卜楞寺是甘南州夏河县旅游的代表，列居格鲁派六大寺院之一，是安多藏区的宗教中心，世界上最大的藏学府。<br>郎木寺：郎木寺是个小镇的名字，有着“东方小瑞士”之称，小镇被白龙江（一条小溪）分为甘川两部，北岸属甘肃碌曲，南岸属四川若尔盖。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">6-8月是来甘南旅行的最佳时节，此时的甘南平均气温为10-20℃，是全年中最暖和的时候，绿草如茵，野花遍布，草场茂盛，色彩斑斓，最能欣赏到高原美景。此外，正月初三至正月十七的祈愿法会期间，有多种藏族特色的节庆轮番登场，对藏族文化及民俗感兴趣的朋友来说，是不能错过的好时候。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">甘南地处高原，昼夜温差大，即使在最暖和的7、8月间，早晚也需要穿抓绒衣、薄毛衣或外套，中午时可穿长短袖T恤。高原紫外线强烈，太阳镜、帽子、防晒霜、雨伞必不可少。正月晒佛节期间的平均温度在-10℃左右，需穿羽绒服、棉毛衣裤等御寒性强的衣物。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">在甘南旅游最大的花销是车费，大多数人选择包车从兰州、夏河、郎木寺等地出发，以线路游玩为主，基本上2-3天的包车费用在1000元以上，但可以多人平分。旺季时住宿标间在100-200元之间，青年旅社或者多人间的价格则在30-50元之间。另外，甘南州因地理位置不利，交通不便，所以蔬菜的价格会偏贵一些。价格参考：牛羊肉40元/斤，面食10-20元/份，荤菜30-50元/份，素菜10-20元/份，藏包约15元/10个，酸奶10-15元/份。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-24 11:22:59', '', '0', 'group1/M01/41/CC/eBif6Fj9bpuAVjhDAAEt8aN7zGE211.jpg', 'group1/M01/41/CC/eBif6Fj9b1mAHcvkAAJqfYOU61w626.jpg,group1/M01/41/CC/eBif6Fj9b1yAF8G7AAGLk_h7-mc053.jpg,group1/M01/41/CC/eBif6Fj9b2CAKmBcAAExLwx8PU4787.jpg,group1/M01/41/CC/eBif6Fj9b2OALVHaAAJ7UiEOBgg085.jpg,group1/M01/41/CC/eBif6Fj9b2eAPcheAASk_-LFLTU596.jpg,group1/M01/41/CC/eBif6Fj9b2-AGidFABYO9QsVX88603.jpg');
INSERT INTO `sys_city` VALUES ('630100', '240', '630000', '西宁', 'XN', 'X', '101.778223', '36.617134', 'https://imgb.mofangx.com/test/29c7ddb4729044d487cc856ae1786e87.jpg', '青海湖,东关清真大寺,马步芳公馆,', '1', '0', '1', '5', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">西宁速览</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">西宁古称“湟中”，是一座具有2100多年历史的高原古城。西宁曾是西汉将军赵充国屯田的基地，是丝绸之路青海道的通衢、沟通中原与西部边地的重要城镇，也是历史上“唐蕃古道”必经之地。今天的西宁市为兰青铁路终点、青藏铁路和青藏公路起点，依然是通往青藏高原腹地的交通要冲。西宁周围群山环抱，冬无严寒，夏无酷暑，气候宜人。夏季凉风习习，凉爽如秋，是天然的避暑胜地。作为一个历史文化名城和旅游城市，西宁市名胜古迹众多，主要游览点有北山寺、东关清真大寺、马步芳宅邸等，湟中县的塔尔寺是藏传佛教圣地，著名的青海湖距西宁市151公里。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">有着“中国夏都”之称的西宁，7月凉爽宜人，是最佳的旅行时间。10月上旬，要是运气好，还能赶上一场初雪，甚至能领略到金色的山野覆盖着白色羽被的美景，还能品尝到青海甘甜的各种瓜果。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">虽冬无严寒，夏无酷暑，但是日夜温差大，所以即使是夏天来西宁旅行，也要备一件秋天时穿的外套。西宁市区中心海拔2261米，要注意防晒，帽子和墨镜必不可少。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">西宁市消费水平一般，经济住宿约200元/天，吃饭人均20-30元，打车市内基本10元以内。但当7、8月旅游旺季时，旅馆可能涨价1-2倍，所以在此期间出行一定要提前预定住宿。此外，在西北旅行花费最多的是包车钱，一般4人拼车约100-200元/天/人。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-21 17:26:21', '', '0', 'group1/M01/41/44/eBif6Fj5ztSAbtEGAAPKytRCVyY888.jpg', 'group1/M01/41/44/eBif6Fj50CGAe3PoACA7xUO8RNs007.jpg,group1/M01/41/44/eBif6Fj50COATr-wAA_II4L6_Pc536.jpg,group1/M01/41/44/eBif6Fj50COAbe_QAAIY6_1jNzM256.jpg,group1/M01/41/44/eBif6Fj50CqAQB5kAB1LCNLqjpk879.jpg,group1/M01/41/44/eBif6Fj50CuATTloAAFT8uu8xXI999.jpg');
INSERT INTO `sys_city` VALUES ('630200', '240', '630000', '海东', 'HD', 'H', '102.115151', '36.498267', '', '', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">海东位于青海湖以东，是一座文化历史悠久的城市，早在新石器石器就有人类居住，曾发现多处类似马家窑文化、齐家文化、辛店文化和卡约文化的遗址。海东民俗文化资源丰富，县境内有民和回族土族自治县、互助土族自治县、循化撒拉族自治县等少数民族自治县。海东旅游资源也极其丰富，有植物王国孟达自然保护区、有青藏高原“绿宝石”之称的互助北山国家森林公园、受藏传佛教和伊斯兰教影响而建的瞿坛寺、文都大寺、班禅故居等著名景点、撒拉族的珍贵历史文物骆驼泉等，是青海湖边一个充满沧桑历史和文化特色的美丽城市。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">5月-10月</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rg