INSERT INTO `sys_city` VALUES ('632500', '240', '630000', '海南藏族', 'HNCZ', 'H', '100.619542', '36.280353', '', '', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">海南藏族自治州位于青海湖之南，是青藏高原的东门户，素有“海藏通衢”之称。海南藏族自治州旅游资源丰富，下辖有“天下黄河贵德清”的贵德、山河壮丽的共和县、倒淌河与日月山齐辉的兴海县、地处黄河九曲十八弯第二弯的同德县和风情淳朴的贵南县。此外还有青海湖、龙羊峽水库、贵德古城、伏俟城、贵德黄河谷地、积石峡、德吉温泉、孟达天池、坎布拉国家森林公园等多处著名景点，是感受黄河文化、欣赏自然美景的理想之地。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">5-10月</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: