INSERT INTO `sys_city` VALUES ('640300', '240', '640000', '吴忠', 'WZ', 'W', '106.198913', '37.997428', 'https://imgb.mofangx.com/test/8e51e42bb0d24321a734b2621a08b5d9.jpg', '青铜峡108塔,青铜峡鸟岛,青铜峡牛首山旅游景区,', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">吴忠市位于宁夏中部，地处宁夏平原腹地，是中国回族主要聚居区之一，城市中造型精美的清真寺随处可见，有东方的“千塔之城”美称。吴忠滨临黄河，历史悠久，是中华文明的发祥地之一，河套文化的重要组成部分。吴忠依托黄河，形成了独具特色的景观和文化，著名景点有中华黄河坛、黄河楼、一百零八塔、金沙湾、同心清真大寺等，此外还有“中国回族建筑文化之乡”、“中国滩羊之乡”、“中国甘草之乡”等多个城市荣誉。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">春秋最佳</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-w