INSERT INTO `sys_city` VALUES ('659003', '240', '650000', '图木舒克', 'TMSK', 'T', '79.074089', '39.868968', 'https://imgb.mofangx.com/test/ee3d59da98fa4959844d89aaac0ca714.jpg', '', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">图木舒克市是新疆维吾尔自治区直辖的县级市，位于新疆西部，塔里木盆地西端，喀什以东，塔克拉玛干沙漠边缘。北邻阿克苏，西邻喀什，是古丝绸之路必经要道。市内有大漠、山脉、原始胡杨林等景观，浓缩了西域自然风光，这里也是刀郎文化的发源地之一。每年图木舒克市都会举办西域美食旅游文化节，吸引大量游客前来。市内有西北地区最大平原水库，库容7亿立方米的小海子水库和永安坝水库。位于小海子水库东岸的“西海湾”是天然的淡水浴场。图木舒克市内还有许多历史文化遗迹，有4座古城遗台和古屯田遗址等多处历史文化景点。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">春秋适宜</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padd