INSERT INTO `sys_city` VALUES ('659004', '240', '650000', '五家渠', 'WJQ', 'W', '87.54324', '44.166756', 'https://imgb.mofangx.com/test/3c62bb8b9c544835b18ff4b7404661c0.jpg', '', '0', '0', '0', '5', '0', '0', '1', '<div id=\"intro_unstrt-desc\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">五家渠市位于新疆维吾尔自治区中部天山山脉北麓、准噶尔盆地东南缘，与昌吉市、乌鲁木齐市相接。该市是新疆维吾尔自治区天山北坡经济腹心地带，也是从乌鲁木齐到古尔班通古特沙漠最近的绿色通道，区位优势十分明显。五家渠市位于天山山脉博格达峰北麓，准噶尔盆地南缘，东、东南、南依次以八一东干排洪沟、八一水库浸没线、八一分支外排沟接南大渠、老龙河、猛进水库东坝保护带、猛进水库浸没线、场界沟为界，与米泉市接壤;南端突出部分东以场界沟、南以和平渠东一支渠、西以和平渠为界，与乌鲁木齐市为邻;西南、西、北依次以乌五公路路南、师直引水渠、头屯河、二屯枯沟、邓家大沟、望杆子至北沙窝到白家海子南端、沿青草岭至黄家梁到老龙河的昌吉与米东区的界点为界，与昌吉市相连。</p></div><div id=\"intro_unstrt-travelseason\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">全年</p></div><div id=\"intro_unstrt-traveltime\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">1天</p></div><div id=\"intro_unstrt-travelweather\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">当地气候</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">陆性干旱气候</p></div>', '0', '2015-05-12 16:16:02', '', '2017-04-25 15:28:57', '', '0', 'group1/M01/41/D2/eBif6Fj--CWATi5xAACn_h3wBwI401.jpg', 'group1/M01/41/D2/eBif6Fj--pqAEwqCAACneFjoyk8835.jpg,group1/M01/41/D2/eBif6Fj--p2AYxmFAAEh6rMWDsI902.jpg,group1/M01/41/D2/eBif6Fj--qCACAO9AASVoxh588U465.jpg,group1/M01/41/D2/eBif6Fj--qSAGSn-AAMDpNTK6so570.jpg');
INSERT INTO `sys_city` VALUES ('710100', '240', '710000', '台北', 'TB', 'T', '121.535', '25.0268', '', '', '1', '0', '1', '8', '0', '0', '1', '<div class=\"part\" id=\"wiki_part_1\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">台北速览</div><div class=\"figure\" style=\"margin: 20px 0px; padding: 0px; word-wrap: break-word; font-size: 12px; color: rgb(153, 153, 153); line-height: 20px;\"><a href=\"http://c4-q.mafengwo.net/s7/M00/20/67/wKgB6lSHv6iAIO3qAAM5Z0UCtF062.jpeg\" target=\"_blank\" title=\"点击查看原图\" style=\"color: rgb(153, 153, 153); text-decoration: none; cursor: pointer;\"><img src=\"http://c4-q.mafengwo.net/s7/M00/20/67/wKgB6lSHv6iAIO3qAAM5Z0UCtF062.jpeg\" style=\"border: 0px; vertical-align: middle; margin-bottom: 8px; max-width: 100%;\"></a><div style=\"margin: 0px; padding: 0px; word-wrap: break-word;\">图：台北车站。By 兔知知</div></div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">或许你最早了解的台北，都是从歌词里面来：“冬季到台北来看雨”、“忠孝东路走九遍”、我想要一个人去阳明山上看海芋，拍偶像剧”。不知道带着情歌与情愫来到台北以后，你看到璀璨的101大楼与隔壁的四四南村、看到诚品书店24小时络绎不绝的读书人、看到夜市里令人垂涎三尺的台式小吃，会不会觉得，这就是你的台北？</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">除此以外，台北故宫中还拥有与北京故宫气质绝然不同的藏品；北投散发着硫磺味道的街头深藏着木造图书馆；国父纪念堂每小时都会上演令人眼花缭乱的换岗秀；永康街上的牛肉面店飘出浓郁汤底的香气；平溪线上既有高傲的猫村，也有寄托着幸福祈望的天灯缓缓升起…… 无论你选择用怎样的方式靠近、了解台北，都会得到一个温柔的答案。</p></div><div class=\"part\" id=\"wiki_part_2\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">3-5月和10-11月是台北的最佳旅游时间。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">3-5月、10-11月：</span>这时候是台北的春秋季，此时台北气候温和舒适，且少雨，天气适宜出行。春天也是阳明山的花季，樱花、杜鹃花、海芋花相继绽放山谷，美不胜收；每年10-11月，台北有百年历史的温泉节也会如期而至，此时去北投泡温泉，还会赶上酒店应季的打折优惠。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">5月底-9月：</span>此段时间为台北的夏季，天气炎热潮湿；同时也是台风季，行前需要查好天气，以免遇到台风天耽误行程。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">12-次年2月：</span>这时台北进入冬季，气温虽然不会太低，但天气潮湿多雨，常会感觉阴冷，需要准备厚一点的长袖外套，以免着凉。</p></div><div class=\"part\" id=\"wiki_part_3\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">建议游玩天数</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"><span class=\"highlight\" style=\"color: rgb(255, 157, 0) !important;\">5天</span></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">如果专程前往台北，这是一个适合你细细品味的城市。市内需要你2－3天的时间来游览，安排好从南往北的顺序，避免出现城市两头跑着追景点的尴尬。但是不得不说颇为让人郁闷的是可能你很难在渔人码头、101大厦、猫空和象山中选择你最倾心的夜景观赏地。而且这里还有无数夜市、商业街、24小时的书店等着你。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">稍远可以前往平溪线一日游，九分的旅行也可以勉强归纳到台北的小旅行中。野柳地质公园颇得好评，建议可以考虑纳入旅行目的地之中。再小众的推荐非北投莫属，这里适合在前往淡水之前打发时间，或者干脆住下来，感受温泉的乐趣。</p></div><div class=\"part\" id=\"wiki_part_4\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">消费水平</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">台北整体的消费水平与大陆一线城市相近，在台湾算是消费较高的城市，尤其是在住宿方面，消费相对偏高。</p><div class=\"h4\" style=\"margin: 20px 0px 0px; padding: 0px; word-wrap: break-word; color: rgb(255, 157, 0);\">餐饮</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">夜市的各种小吃大多20元/份；一碗台湾牛肉面大约40元人民币。</p><div class=\"h4\" style=\"margin: 20px 0px 0px; padding: 0px; word-wrap: break-word; color: rgb(255, 157, 0);\">住宿</div><table class=\"table\" style=\"border-collapse: collapse; border-spacing: 0px; margin: 20px 0px; width: 667px; border: 1px solid rgb(224, 224, 224); text-align: center;\"><thead><tr><th style=\"padding: 20px 15px; color: rgb(255, 255, 255); font-weight: normal; background-color: rgb(97, 197, 255);\">高档酒店</th><th style=\"padding: 20px 15px; color: rgb(255, 255, 255); font-weight: normal; background-color: rgb(97, 197, 255);\">经济型</th><th style=\"padding: 20px 15px; color: rgb(255, 255, 255); font-weight: normal; background-color: rgb(97, 197, 255);\">青年旅舍</th></tr></thead><tbody><tr><td style=\"color: rgb(51, 51, 51); padding: 15px; line-height: 20px; border-bottom: 1px solid rgb(238, 238, 238);\">800元以上</td><td style=\"color: rgb(51, 51, 51); padding: 15px; line-height: 20px; border-bottom: 1px solid rgb(238, 238, 238);\">500－800元</td><td style=\"color: rgb(51, 51, 51); padding: 15px; line-height: 20px; border-bottom: 1px solid rgb(238, 238, 238);\">200元左右</td></tr></tbody></table><ul style=\"margin: 0px; padding: 0px; list-style: none;\"><li style=\"margin: 0px; padding: 0px; list-style: none;\">以上价格为单晚、标准间单间价格</li><li style=\"margin: 0px; padding: 0px; list-style: none;\">青年旅舍为单晚／床价格</li><li style=\"margin: 0px; padding: 0px; list-style: none;\">结算单位为人民币</li></ul><div class=\"h4\" style=\"margin: 20px 0px 0px; padding: 0px; word-wrap: break-word; color: rgb(255, 157, 0);\">交通</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">台北市内交通以捷运和公交为主，价格和国内一线城市相当。</p><div class=\"h4\" style=\"margin: 20px 0px 0px; padding: 0px; word-wrap: break-word; color: rgb(255, 157, 0);\">小费</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">这里并没有给小费的习惯。但在部分酒店和一些高档餐厅会收取10%的服务费。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-17 09:49:17', '', '0', 'group1/M01/46/3D/eBif6FlsEsWATqMGAAFt4CXR4Zc570.jpg', 'group1/M01/46/3D/eBif6FlsF3aAfVxcAAEOlYPBXFw720.jpg,group1/M01/46/3D/eBif6FlsF3mAQiD6AAImYPVoXcs814.jpg,group1/M01/46/3D/eBif6FlsF36AaVhkAAG5iV40AtI742.jpg,group1/M01/46/3D/eBif6FlsF4GALmMvAAFCP7mriDU534.jpg,group1/M01/46/3D/eBif6FlsF4aAGr4gAAG8KdV8t9g838.jpg,group1/M01/46/3D/eBif6FlsF4uAENqSAAHVVhWMo68268.jpg,group1/M01/46/3D/eBif6FlsF46Aba9QAAIXym7m4FI102.jpg,group1/M01/46/3D/eBif6FlsF5GAZtsyAAEMo2oC8kM434.jpg,group1/M01/46/3D/eBif6FlsF5WASZwaAAKoS5GQQ5g240.jpg');
INSERT INTO `sys_city` VALUES ('710200', '240', '710000', '高雄', 'GX', 'G', '114.311982', '25.117753', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">高雄速览</span></font></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">坐落于台湾南部西南隅的高雄，其实并不像她的名字那么伟岸，常常有人将台北比作一位优雅的绅士，那高雄则是一位仪态万千的女士。高雄终年空气潮湿，光照充足，专有的热带季风吹过，绿树成荫，花香袅袅，街道狭窄，宁静的爱河从城中穿过，河畔夜景尤其动人。</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\"><strong>西子湾风景区：</strong>高雄必游的代表景点，也是南台湾最富盛名的观光地区。<br><strong>美丽岛捷运站：</strong>被誉为“世界最美的地铁站”之一，《痞子英雄》曾在此取景。<br><strong>高雄85 大楼：</strong>南台湾的地标，也是台湾第2 高楼，仅次于台北101。<br><strong>莲池潭风景区：</strong>高雄市左营区内最大的湖泊，每年10 月会举办一年一度的“莲池潭万年祭”。<br><strong>寿山风景区：</strong>被称为“高雄之肺”的原始绿林，可赏独特的台湾魔芋与密毛魔芋。<br><strong>六合夜市、瑞丰夜市：</strong>当地人气最旺的美食街夜市，可吃到正宗的高雄小吃，价格经济实惠。<br><strong>打狗英国领事馆：</strong>台湾第一栋建造完整坚固的洋房，如今是下午和夜晚休闲的绝佳之地。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">高雄一年四季都适宜旅游，如果赶上端午节和灯会期间会更热闹有趣。不过每年7-9 月是当地频繁遭遇台风侵袭的时节，若计划此季出行，一定要事先查询天气预报，以免耽误行程。?</span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"color:#f87a7a\"><span style=\"font-size: 18px;\">穿衣指南</span></span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">高雄2 月平均气温为18.6℃，为全年最低，旅行者此时前往可穿一件T 恤外加一件外套。7 月平均气温为28.7℃，为全年最高，此时出行只需一件短袖T 恤之类的清凉着装即可，但要注意防晒。</span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">消费水平</span></font></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">高雄的消费水平比北京、上海等一线城市略低。一个盒饭大约是60-100 新台币（即约15-20 元人民币），一碗牛肉面约20-25 元人民币。在高雄的六合夜市，人均消费70元左右就能吃撑；饭店和住宿的价格也是和大陆相似，有时会略高一些。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">其他</span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span></p><p>货币</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\">在台湾旅游中，都以新台币（缩写为：TWD、NT或NTD、NT$）使用为原则，信用卡也可通用。台湾货币中，1000 元、500元、100元为纸币；50元、20元、10元、5元、1元为硬币。去台湾旅游，带上一张银联卡即可。台湾目前有80%的ATM机有银联标示，可直接用银联卡取款，可以到7-11、全家便利店或邮局寻找带有银联标示的ATM。<br>参考汇率（2015 年3月汇率）：1人民币元= 5.0547新台币</p><p>小费</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\">旅客在进入旅馆办理入住手续时，随身大型行李应由服务生代送至房间，依惯例，均会给予小费，每间房间每次约台币50元或美元2元。在台湾的一些大型旅馆、餐厅都会收取10%的服务费。</p><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span><br></p><p style=\"line-height: 1.75em; color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-bottom: 10px; text-align: center;\"></p>', '0', '2015-05-12 16:16:02', '', '2017-07-18 16:41:13', '', '0', 'group1/M01/46/45/eBif6FltyRWAVwXzAAE_woJvaVE058.jpg', 'group1/M01/46/45/eBif6FltyRuAFUTAAAJovCntBz4319.jpg,group1/M01/46/45/eBif6FltyR2AautgAAQSw48T4TA110.jpg,group1/M01/46/45/eBif6FltyR-AH_xIAAOujr9pbNI865.jpg,group1/M01/46/45/eBif6FltySGATJe0AAGpbj7wFA0775.jpg,group1/M01/46/45/eBif6FltySSAXukqAARJU-C-o_8332.jpg,group1/M01/46/45/eBif6FltySaAaPzEAAMeIGNLh4I721.jpg,group1/M01/46/45/eBif6FltySiARawuAARBEi7v7S8995.jpg');
INSERT INTO `sys_city` VALUES ('710300', '240', '710000', '台南', 'TN', 'T', '120.29334', '23.143782', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"text-align: justify; color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\"></span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">台南位于台湾西南部的嘉南平原，西临台湾海峡、东依阿里山山脉、北接嘉义县、南与高雄市接壤，又因拥有优良的大型海湾，旧时为大陆闽、粤渔民和澎湖渔民的天然避风港。台南原名赤崁，因高山族西拉雅平埔人赤崁社得名；在清朝年间曾是台湾的行政中心，昔日“商贾日盛，市街纷错”。有学者曾说，如果想了解台湾的历史，来台南就好了。这里承载着时光对这座岛屿的侵袭，留下光阴拂过的痕迹。<br>身为台湾最早发展的城市，这座古城已经有数百年的历史，而因为独特的文化和历史背景，台南有一种闲适却又优雅的感觉，像是文人雅士般，却又带著台湾南部特有的热情。在这座曾经的“府城”里，遍布着百余处大大小小的明清建筑——夕阳下安平古堡泛着红光的城墙，就是台南最美的写照。而如今的台南，平静的街道上，穿梭着行色匆匆的上班族；汽车的鸣笛声唤醒了布满岁月痕迹的老城；新与旧的融合、台客文化与世界文创互相影响，使台南这个有着悠久文化的大古都焕发出不一样的光彩。而大天后宫门前，那一对斑驳的石狮，始终见证着这座古城沉淀百年的情怀。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">来台南旅游的最佳时间为春秋两季，尤以10-11月为佳，此时气温适宜，更好能感受台南的传统氛围。?</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">穿衣指南</span></font></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">9-11月期间来台南，此时平均气温在24-26℃左右，降雨量少，气候舒适宜人，可以携带短袖和长裤，再带一件轻薄外套备用。而12月-次年2月间的平均气温在18℃左右，建议穿着长裤和外套，另带一件中等厚度的外套以备用。夏季5-9月，台风多发，降雨量较大，不建议前往；但若此时到访台南，除穿着短裤短袖外，建议一定要携带雨衣、雨伞和备用衣物、鞋子，应对突然天气。</span></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 10:55:26', '', '0', 'group1/M01/46/48/eBif6FluyfmANFPeAAEwac_PTWk238.jpg', 'group1/M01/46/48/eBif6Fluyf-AIbv-AAKGIFvNHe8679.jpg,group1/M01/46/48/eBif6FluygKAWobyAAOr082snqI354.jpg,group1/M01/46/48/eBif6FluygaAWFDiAAM2nOHtDKc066.jpg,group1/M01/46/48/eBif6FluygmARehIAAIx1nlerNE831.jpg,group1/M01/46/48/eBif6FluyguAatqqAAINWTq5uXk857.jpg,group1/M01/46/48/eBif6FluyhCANBrVAAR8tVx-cmg173.jpg,group1/M01/46/48/eBif6FluyhOABq2SAAOBz3qdd_k920.jpg');
INSERT INTO `sys_city` VALUES ('710400', '240', '710000', '台中', 'TZ', 'T', '120.718137', '24.241978', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">台中速览</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">台中无疑是个幸运儿，它拥有一年四季舒爽的天气，清风宜人，花香四溢；它身在岛屿却不受台风的打扰，耸立在东面的中央山脉像英勇的卫士，阻挡了太平洋的凛冽季风。</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">这里是台湾最适宜居住的城市，是人们眼中山川城市共荣的“宁静之都”。你可以在台湾美术馆欣赏无价的画作；在自然科学博物馆探究神奇的世界；在高美湿地追逐夕阳的尽头；在宫原眼科吃一口奶味浓郁的冰淇淋；穿过彩虹眷村，亲手拍下那些童话般绚丽的涂鸦，一切都是那么轻松又惬意。这就是即使走后，仍然让人无法忘怀的，美丽又热情的台湾之心。</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\"><strong>台中车站：</strong>是台中重要的交通枢纽，也是台中很有名的历史古迹！<br><strong>东海大学：</strong>是全台湾校园面积最大和建筑风格最独特的大学之一，其中的路思意教堂是游客必踩的点！<br><strong>高美湿地：</strong>夕阳下的高美湿地总是位于“台中最美景”的榜首，不过美景也是需要看天气哦！<br><strong>逢甲夜市：</strong>全台中最大的夜市，每天吸引着全世界各地喜欢美食的游客，无论你的品味多独特，总有适合你的美食！<br><strong>彩虹眷村：</strong>这条村子是充满着色彩与图画的世界，让人暂时忘记了烦恼，在这片乐园里回归最稚嫩的自己。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">台中是名符其实“四季如春”的城市，一年四季都适合来旅行。它地处台湾的中西部，最寒冷的1月份，平均气温也在16度，此时的台中并没有因为冬季而缺少了它的趣味，你一样可以穿着薄薄的衣服到宫原眼科吃不同口味的冰淇淋或者到东海大学躺在草地上看着蓝天白云和金三角似的路思义教堂。</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">在最炎热的7月，你可以到高美湿地吹吹风看看夕阳下的大风车，或者到逢甲夜市左手一串右手一袋地平常美食。虽然台湾岛6-9月是台风季，但由于受到中央山脉群的保护，台中受到的影响不大，大家还是可以快乐地玩耍哦！</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">穿衣指南</span></font></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">如果12月-次年3月间前往台中，正是台中相对气温较寒冷的季节，不过平均气温也在18度左右，所以需要穿着长裤长袖，携带中等厚度的外套；如果5月-10月前往，台中的平均温度基本比较稳定，在27度左右，6月-8月会出现32度左右的天气，穿着长裤和短袖，或者短裤和短袖均可，建议带上一件薄外套，以防入夜后的凉爽天气或者刮风天气。?</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">消费与汇率</span></font></p></section><p style=\"text-indent: 2em;\">消费水平</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">台中市区的大部分住宿酒店价格在400元人民币左右/晚/标间，有部分500-1000元人民币/晚的中高档酒店，也有低于400元人民币/晚的便捷旅店和饭店可以选择。餐饮方面，在逢甲夜市花上50元人民币，你就可以饱餐一顿了；如果选择一些早餐店、特色餐厅等，人均50-70元也能吃得非常满足了。台中很多景点都是免费参观，收取门票的景点，一般人均在20-50元人民币，还是比较合理的。</p><p style=\"text-indent: 2em;\">货币</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">台湾流通货币为新台币(简称TWD、NT或NTD)。去台湾旅游，带上一张银联卡即可。台湾目前大部分的ATM机有银联标识，可直接用银联卡取款，在各大便利店或邮局都能找到带有银联标识的ATM（华夏等银行发行了境外ATM取款免手续费的银联卡，具体可咨询各大银行）。</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">参考汇率（2015年9月）：1新台币=0.1957人民币元，1人民币元=5.1104新台币</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\"><br></p><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 11:20:18', '', '0', 'group1/M01/46/48/eBif6Fluy4iAc_gPAACn4_uk3mY029.jpg', 'group1/M01/46/48/eBif6Fluzx6ATJi6AAQ51ooJZI4089.jpg,group1/M01/46/48/eBif6Fluzx-AYwVlAAMy6qQoRj8658.jpg,group1/M01/46/48/eBif6FluzyWAJTdPAAKf-YdS-_Y317.jpg,group1/M01/46/48/eBif6FluzymAIvBhAAWG0XqkkrQ596.jpg,group1/M01/46/48/eBif6FluzyuAAW0PAADdc_3IfQI525.jpg,group1/M01/46/48/eBif6Fluzy-ALH8VAANiHRPzCa0053.jpg');
INSERT INTO `sys_city` VALUES ('710500', '240', '710000', '金门', 'JM', 'J', '118.317089', '24.432706', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\"><span style=\"font-family: 宋体, SimSun;\">&nbsp;</span></section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A; font-family: 宋体, SimSun;\">简介</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun;\">金门县位于福建省东南海上，以“固若金汤、雄镇海门”之意而称“金门”。位于福建省东南部海域泉州围头湾与厦门湾内，屹立于台湾海峡西部，与中国大陆最近处仅2310米。金门，不是你想去，买张船票就可以去的地方，金门，隶属我们福建省，但归台湾管，台有派屯兵驻守。因此，到金门，要办理出境手续，办理完，持旅行社提供的《大陆居民申请入金门旅行团体名册》和在出入境管理科办理的《大陆居民往来台湾通行证》，到泉州石井港口或者厦门五通等码头，购买船票。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\"><span style=\"font-family: 宋体, SimSun;\">&nbsp;</span></section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A; font-family: 宋体, SimSun;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun;\">10月是前往金门的最佳旅游时间，那段时间金门不会太冷也不会太热，温度适宜，而且很多商店都在打折。</span></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 15:16:22', '', '0', 'group1/M01/46/4B/eBif6FlvBbCAZMmLAAB1LtHp9As177.jpg', 'group1/M01/46/4B/eBif6FlvBweAQkBtAAPU78L9zEg623.jpg,group1/M01/46/4B/eBif6FlvBw2AWNklABiQLYdMoZo871.jpg,group1/M01/46/4B/eBif6FlvBxGAfzxFAA2rMs9r6Zw539.jpg,group1/M01/46/4B/eBif6FlvBxKAJb4JAAKTqyHL58U623.jpg');
INSERT INTO `sys_city` VALUES ('710600', '240', '710000', '南投', 'NT', 'N', '120.688592', '23.901028', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">位于台湾地理正中心的南投，是全台湾唯一没有海岸线的县。这里四季气候宜人，春夏秋冬，美景如画，晨昏晴雨，景色变幻无穷，这不仅仅是对巍巍日月潭的描述，更是南投的真实写照。<br>&nbsp; &nbsp; &nbsp; &nbsp;这里有着得天独厚的自然资源，除了美丽得不可言喻的日月潭和有“小瑞士”之称的清境农场，还有台湾最高峰玉山、最长河流浊水溪都位于南投县。怀旧的集集铁路线，雾社抗日纪念碑，古怪又可爱的妖怪村，热情简朴的民俗等，赋予了南投另一番人文气息。这里独特的气候和优良的水质，造就了喷香的红薯和浓郁的绍兴酒。南投就像一潭碧水，洗去游人心中浮华，留下一捧清泉。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">南投县位于台湾岛的中心，是台湾唯一一个没有海岸线的县市。这里年平均气温19.2℃，一年四季温度都相对舒适，都可以前往旅游。</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">4-5 月到清境农场可看到一片青葱的草坪，到日月潭可参加夜间赏萤活动；5 月是清境农场野菊花、杜鹃花的花期；6-8月是清境农场一年一度剪羊毛的季节，还可以采摘水蜜桃；9-11 月为清境农场丰收的季节，可以吃到新鲜水果，品尝首拨冬茶；到日月潭可以参加万人泳渡日月潭比赛或者观看一年一度的烟花表演；12 月- 次年2 月清境农场有绽放的梅花，2 月有盛开的樱花和桃花（花期为2-3月）。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"color:#f87a7a\"><span style=\"font-size: 18px;\">穿衣指南</span></span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">如果11 月- 次年1 月间前往南投，最低气温在15℃上下，可以穿着长裤长袖、携带风衣、中等厚度的外套备用；如果6-8 月前往，此时雨季潮湿、天气较为闷热，最高气温可达30-32℃，所以建议穿着短袖和短裤。可以随身携带驱蚊水或者其他的清凉药物，到草地或者丛林地方最好穿着浅色的长袖长裤，最好准备一把雨伞既可遮阳也可以挡雨；其他时间前往可以携带一套长裤长袖，一套短裤短袖备用，建议再携带一件薄外套应对突发天气。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"color:#f87a7a\"><span style=\"font-size: 18px;\">消费水平</span></span></p></section><p style=\"text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\">南投的景点，如日月潭、清境农场等，住宿和餐饮都相对昂贵，住宿价格平均在30000-4000NTD/ 晚，每顿饭人均消费在400-500NTD。如果选择埔里等地住宿，会相对便宜，民宿每晚价格在1000NTD/ 晚，每顿饭人均消费在250NTD。</span></p><p style=\"text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\"><br></span></p><p style=\"text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\"><br></span></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 14:14:27', '', '0', 'group1/M01/46/4A/eBif6Flu9uuARzt2AACBhrlUZVU188.jpg', 'group1/M01/46/4A/eBif6Flu-BiAD3DNAAGnqi8_eUc083.jpg,group1/M01/46/4A/eBif6Flu-BqAP82dAAJr2r8CvTI959.jpg,group1/M01/46/4A/eBif6Flu-BuAUE49AABV1apune4517.jpg,group1/M01/46/4A/eBif6Flu-B2AdOvnAAGXZEfW73Q659.jpg,group1/M01/46/4A/eBif6Flu-CCANBd9AAHQg5Xr4yo892.jpg,group1/M01/46/4A/eBif6Flu-CKABuzOAAU6_9Nw6J4536.jpg,group1/M01/46/4A/eBif6Flu-CeAS9_GAAhUUwZFzgA539.jpg');
INSERT INTO `sys_city` VALUES ('710700', '240', '710000', '基隆', 'JL', 'J', '85.297535', '28.852394', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">简介</span></font></p></section><p style=\"text-indent: 0em;\"><span style=\"color: #666666; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\">&nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\">基隆市，古名鸡笼，位于台湾本岛北端，是台湾地区五市之一，以深水谷湾之天然良港著名。基隆是台湾最北端的城市，而有“台湾头”之称，因冬季多雨，又被称为“雨港“。辖下有仁爱、安乐、信义、七堵、中山、中正、暖暖等七个区，境内岛屿、港湾、山陵兼具，繁华的港都隐藏在山海之间，基隆港为台湾北部最重要的商港。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">内部交通 </span></p></section><p>出租车</p><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px;\">基隆出租车起步价是70台币，每跳一次表加5台币。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"color:#f87a7a\"><span style=\"font-size: 18px;\">外部交通</span></span></p></section><p>汽车</p><p style=\"margin-top: 15px; margin-bottom: 15px;\">可以乘坐国光号客运从台北、新竹、台中、高雄等城市前往基隆。台北到基隆的车程是50分钟，票价大约是55台币。</p><p>火车</p><p style=\"margin-top: 15px; margin-bottom: 15px;\">台湾西部沿线的火车线路、东部干线都经过基隆。火车都停靠在基隆站。</p><p>船</p><p style=\"margin-top: 15px; margin-bottom: 15px;\">基隆是台湾有名的港口，可以从厦门、澎湖这两地搭船前往。其中厦门的是在厦门轮渡码头乘坐中远之星渡轮，全长12小时30分钟。澎湖的则是乘坐丽星邮轮，全长16个小时，但是这整趟行程都包含在了3天2晚的游轮行程中。</p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><br></p><p style=\"margin-top: 15px; margin-bottom: 15px;\"><br></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 09:55:04', '', '0', 'group1/M01/46/47/eBif6FluupqAdCjjAABV6x_3bJk284.jpg', 'group1/M01/46/47/eBif6FluuqGAM0AJAAOlvB0SHIM396.jpg,group1/M01/46/47/eBif6FluuqSAQcH2AAQaF4B3wrM954.jpg,group1/M01/46/47/eBif6FluuqaAUuL2AALh_kkhqyc301.jpg,group1/M01/46/47/eBif6FluuqeAJJhOAAC63YbQDBk064.jpg,group1/M01/46/47/eBif6FluuquAISyuAAVkL9mvcis197.jpg,group1/M01/46/47/eBif6Fluuq2AWQn7AACrfGr4Hr0065.jpg,group1/M01/46/47/eBif6Fluuq-ARCt2AAJO7EBEF1M816.jpg,group1/M01/46/47/eBif6FluurWALLgGABC3GJ6fVbs933.jpg');
INSERT INTO `sys_city` VALUES ('711100', '240', '710000', '新北', 'XB', 'X', '121.489976,25.087701', '', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\"><span style=\"font-family: 宋体, SimSun;\">&nbsp;</span></section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A; font-family: 宋体, SimSun;\">简介</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun;\">新北，就是人们曾经熟知的台北县。2010“台北县”正式改制升格为院辖市，定名为“新北市”。新北位于台湾的最北端，它温柔地将台北市拥入怀中。基隆、宜兰和桃园则与它紧密相邻。与台北市的光鲜繁华相比，新北则呈现出完全不一样的质朴无华。这里有九份的狭窄石阶，有夕阳下淡水码头的点点辉光，有平溪铁路上缓慢驶过的彩绘火车，有野柳地质公园里威风凛凛的女王头。新北如同天然去雕饰的美女，用最本来的面貌，接受着岁月的洗礼和崇拜者的到访。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\"><span style=\"font-family: 宋体, SimSun;\">&nbsp;</span></section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A; font-family: 宋体, SimSun;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\"><span style=\"font-family: 宋体, SimSun;\">新北紧邻台北市，最佳时间为春秋两季，尤以10-11月为佳，此时气温在22℃—24℃左右，阴雨天气比较少，舒适宜人。每年5月底至9月为夏季，炎热潮湿；12-2月为冬季，气温虽然不会太低，但潮湿多雨，给人的感觉非常阴冷。另外，每年6-10月是台湾的台风季节，其中7-9月台风最为频繁，建议来新北旅游最好错开这一时段。</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\"><span style=\"font-family: 宋体, SimSun;\">&nbsp;</span></section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A; font-family: 宋体, SimSun;\">穿衣指南</span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: 宋体, SimSun;\">整个台湾终年气候宜人，冬无严寒，夏无酷热。即使在冬季，相对大陆北方的天气，也较为温暖舒适的，平均气温在15度上下，只需准备简单的羊毛衣物及外套即可；夏季多雨闷热，只需穿棉质轻便衣服或T恤短裤；春秋两季晚间稍凉，可在T恤外面加一件薄外套。</span></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 15:50:47', '', '0', 'group1/M01/46/4C/eBif6FlvCvmAAvUuAAImMxk0IJg399.jpg', 'group1/M01/46/4C/eBif6FlvD06ARFddAAMK--x0geM679.jpg,group1/M01/46/4C/eBif6FlvD0-AG8T9AAG3OWd8zyY058.jpg,group1/M01/46/4C/eBif6FlvD1GAVctaAAQH8tmjjco744.jpg,group1/M01/46/4C/eBif6FlvD1OAQ51mAAL8i1ft0xw812.jpg,group1/M01/46/4C/eBif6FlvD1eABv6gAAir2tNSK6Y587.jpg');
INSERT INTO `sys_city` VALUES ('711200', '240', '710000', '宜兰', 'YL', 'Y', '121.759615', '24.750696', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">位于中国台湾省东北部，是个多山的县，山的环抱致使兰阳平原形如畚箕，在东北季风的吹拂下，宜兰尽揽雨水湿气，故人称\"竹风兰雨\"。宜兰的著名景点有龟山岛、太平山森林游乐区、开兰吴宅、五旗峰瀑布、苏澳、礁溪温泉、头城海水浴场、鸳鸯湖等。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">夏季是前往宜兰的最好时间。宜兰风景多以自然美景为主，夏季的时候正好枝繁叶茂，天气也好。冬季因为风多雨多不太合适。</p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 12:01:33', '', '0', 'group1/M01/46/49/eBif6Flu1m2AEzW-AAC2adcITdU302.jpg', 'group1/M01/46/49/eBif6Flu2J2AfNJXAALuLBABkr0935.jpg,group1/M01/46/49/eBif6Flu2J-AIbPRAADsqjIzRSw859.jpg,group1/M01/46/49/eBif6Flu2KOAG1NNAAaT95xXP0c886.jpg,group1/M01/46/49/eBif6Flu2KaAPfHNAAOK_9gz6DE916.jpg,group1/M01/46/49/eBif6Flu2KmAGFcsAAGoOzRpuoA054.jpg,group1/M01/46/49/eBif6Flu2K6ABoAJAAOFY5MaykM811.jpg,group1/M01/46/49/eBif6Flu2LOADud2AAD5u4zJ3Tk814.jpg,group1/M01/46/49/eBif6Flu2LWAYCbBAAJKWfmeA3A563.jpg,group1/M01/46/49/eBif6Flu2LyARlFmAB9TRIQ8bNk246.jpg');
INSERT INTO `sys_city` VALUES ('711300', '240', '710000', '新竹', 'XZ', 'X', '120.967397', '24.808312', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">台中速览</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">新竹县于台湾西北部，北临桃园县，南接苗栗县，西部面向台湾海峡，全县多为丘陵、台地及山地地形。曾经隶属于新竹县管辖的新竹市，已经于1982年正式脱离新竹县的版图，成为独立的省辖市；现在新竹县的政治中心，已经转移至竹北乡。新竹县的所在地，曾是原住民道卡斯平埔族、赛夏族、泰雅族的世代聚居之地；随着岁月的变迁，如今这里已是客家人的主要居住地。而受到邻居新竹市的影响，新竹县也逐渐展现出高新科技发展区的雏形，众多科技大学都纷纷在此落户。新竹县的旅游资源以原住民文化、古朴老街、人文博物馆及各种自然风光为主，浓浓的复古风情是这里新竹不变的主题。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">来新竹旅游，最佳时间为春秋两季，尤以10-11月为佳，此时气温在22℃—24℃左右，阴雨天气比较少，舒适宜人。每年5月底至9月为夏季，炎热潮湿；12-2月为冬季，气温虽然不会太低，但潮湿多雨，给人的感觉非常阴冷。另外，每年6-10月是台湾的台风季节，其中7-9月台风最为频繁，建议来台湾旅游最好错开这一时段。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><font color=\"#f87a7a\"><span style=\"font-size: 18px;\">穿衣指南</span></font></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">新竹的气候与整个台湾相仿，终年温度宜人，冬无严寒，夏无酷热。即使在冬季，相对大陆北方的天气，也较为温暖舒适的，平均气温在15度上下，只需准备简单的羊毛衣物及外套即可；夏季多雨闷热，只需穿棉质轻便衣服或T恤短裤；春秋两季晚间稍凉，可在T恤外面加一件薄外套。?</span></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 11:32:21', '', '0', 'group1/M01/46/48/eBif6Flu0K6AKWqWAAFHWIkaX-Y666.jpg', 'group1/M01/46/48/eBif6Flu0nWAZQUFAAFRROp-LZo397.jpg,group1/M01/46/48/eBif6Flu0neAV08tAACiX3dQfAc757.jpg,group1/M01/46/48/eBif6Flu0nmALCqUAALJhApUgVI895.jpg,group1/M01/46/48/eBif6Flu0nuAPL1SAAKWxaTnWs4929.jpg,group1/M01/46/48/eBif6Flu0nyAFL3CAAJG67MrsfY248.jpg,group1/M01/46/48/eBif6Flu0n-AA8-zAAS7UzDxU6o846.jpg,group1/M01/46/48/eBif6Flu0oGAJX3-AAJVQKPA4n8987.jpg');
INSERT INTO `sys_city` VALUES ('711400', '240', '710000', '桃园', 'TY', 'T', '121.16337', '24.926517', '', '', '0', '0', '0', '8', '0', '0', '1', '<div id=\"intro_30650\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">简介</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">桃园县位于台湾岛的西北部，临山面海，无论是台地还是市区，都能让你在自然风光与人文地理中寻一处诗性的游览之地。风景区主要有石门水库、角板山公园、虎头山公园等，人文景点主要集中在东南部的大溪、龙潭、复兴等乡镇，如大溪老街、李腾芳古宅、齐明寺、莲座山观音寺等文史古迹，以及著名的大溪豆干、木器等。北横公路是本县最精彩的旅游动线，串连起慈湖、角板山风景区、东眼山森林游乐区、小乌来风景区，以及巴陵一带的达观山自然保护区、嘎拉贺温泉等，而夏季碰上水蜜桃产期，更可大啖一番。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_30651\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">语言</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">台湾最常用的语言为国语和闽南语。其中北部地区多使用国语，而中南部地区使用国语和闽南语，不过在台湾各地，使用国语基本都能沟通无障碍。除了常用的国语和闽南语，台湾各地还有一些当地的语种，比如原住民的语言、客家话等等，但是大部分原住民也都可以用国语交流。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_30652\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">最佳旅行时间</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">10-11月是最佳旅游时间，这段时间去桃园气温舒适，太阳也不大，也不会赶上暴风雨。</p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p></div><div id=\"intro_30653\" class=\"part\" style=\"margin: 0px 0px 50px; padding: 0px 0px 1px 20px; word-wrap: break-word; border-left: 2px solid rgb(238, 238, 238); font-size: 14px; color: rgb(102, 102, 102); line-height: 20px; font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; widows: 1;\"><div class=\"h2\" style=\"margin: 0px 0px 0px -22px; padding: 0px 0px 0px 20px; word-wrap: break-word; color: rgb(51, 51, 51); height: 29px; line-height: 28px; font-size: 18px; border-left: 2px solid rgb(255, 157, 0);\">穿衣指南</div><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\"></p><p style=\"margin: 15px 0px; padding: 0px; word-wrap: break-word;\">台湾终年气候宜人，冬无严寒，夏无酷热。如果冬季前往，可以携带毛衣和厚实的外套；如果3、4月份或者9-11月秋季前往，可以穿薄长袖、长裤，再携带一件薄外套，以防夜晚寒冷；夏季6-9月不建议前往，因为此时正是雨季和台风季，如果必须前往，建议穿着短裤短袖，携带防晒衣，做好防晒措施，携带帽子、墨镜等。</p></div>', '0', '2015-05-12 16:16:02', '', '2017-07-17 10:32:08', '', '0', 'group1/M01/46/3D/eBif6FlsHBWAOwhmAAJC0spdL9Y071.jpg', 'group1/M01/46/3D/eBif6FlsIXiAAvQ1AALDE5A0qac700.jpg,group1/M01/46/3D/eBif6FlsIX-AYxofAAOC0Fuy1W4147.jpg,group1/M01/46/3D/eBif6FlsIYCAH_J9AAE_ZuNp8Wo933.jpg,group1/M01/46/3D/eBif6FlsIYOAcuFsAAGo62YhKMI686.jpg,group1/M01/46/3D/eBif6FlsIYiAJTLWAAFQww5zXu8570.jpg,group1/M01/46/3D/eBif6FlsIYWARWSfAAHNIi6Uauc799.jpg,group1/M01/46/3D/eBif6FlsIYqABa-1AAIBfjwsmuY485.jpg,group1/M01/46/3D/eBif6FlsIYuAIVg-AAFf7f9TO5Q277.jpg,group1/M01/46/3D/eBif6FlsIaWAF3-GAAGvmBkXC6A297.jpg');
INSERT INTO `sys_city` VALUES ('711500', '240', '710000', '苗栗', 'ML', 'M', '120.819288', '24.561582', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">苗栗县位于台湾本岛中北部，北边和东北边与新竹县、新竹市为邻，西滨台湾海峡。全县海岸线长度约50公里，面积1820.31平方公里，外型颇似一颗闪耀的钻石。这里是台湾有名的“客家大县”，县内的18个乡镇市中，13个都是客家人的主要居住地；各种客家小镇和客家大院，是苗栗县最独特的旅游资源。苗栗县的经济活动曾以农牧业为主，时至今日，各种蔬果农场和牧场，仍然是游客们最爱的观光胜地。各种人文民俗博物馆是苗栗县旅游的又一大特色；如果除了人文风情，你更偏爱古早情怀，那么胜兴车站和龙腾断桥等景点，一定会满足你的需要。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">旅游苗栗，最佳时间为春秋两季，尤以10-11月为佳，此时气温在22℃—24℃左右，阴雨天气比较少，舒适宜人。每年5月底至9月为夏季，炎热潮湿；12-2月为冬季，气温虽然不会太低，但潮湿多雨，给人的感觉非常阴冷。另外，每年6-10月是台北的台风季节，其中7-9月台风最为频繁，建议来台北旅游最好错开这一时段。</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"color:#f87a7a\"><span style=\"font-size: 18px;\">穿衣指南</span></span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em; text-indent: 2em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">台北终年气候宜人，冬无严寒，夏无酷热。即使在冬季，相对大陆北方的天气，也较为温暖舒适的，平均气温在15度上下，只需准备简单的羊毛衣物及外套即可；夏季多雨闷热，只需穿棉质轻便衣服或T恤短裤；春秋两季晚间稍凉，可在T恤外面加一件薄外套。</span></p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 13:47:46', '', '0', 'group1/M01/46/49/eBif6Flu8faAat3aAAC3AZ4ziZU225.jpg', 'group1/M01/46/49/eBif6Flu8fiAAdzzAAHxfIzzgP0115.jpg,group1/M01/46/49/eBif6Flu8fqAA_cqAAOdZs2K-rk675.jpg,group1/M01/46/49/eBif6Flu8fyAUds_AAIb28U5bH0121.jpg,group1/M01/46/49/eBif6Flu8f6AI3e2AAKc32sxQiw455.jpg,group1/M01/46/49/eBif6Flu8gGAZO8HAAVA-VA5i9E441.jpg,group1/M01/46/49/eBif6Flu8gSASnj-AAD4BVN9rPo138.jpg,group1/M01/46/49/eBif6Flu8geAOyASAABEQa-tQCk043.jpg');
INSERT INTO `sys_city` VALUES ('711700', '240', '710000', '彰化', 'ZH', 'Z', '120.542148', '24.081026', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"margin-top: 15px; margin-bottom: 15px; color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti; font-size: 14px; text-indent: 2em;\">彰化县位于台湾中部，西临台湾海峡，东以平缓的八卦山脉和南投相邻，北以大肚溪和台中相接，南部则和云林县以浊水溪相隔，幅员完整，地势平坦开阔，属嘉南平原的一部分，土地肥沃，物产丰饶，素有台湾谷仓之称。面积有1074.4平方公里，人口有12万，地形多以平原为主。这里有好多景点：如彰化地标的八卦山大佛、鹿港昔日辜显荣旧居的民俗文物馆、有台湾紫禁城之称的龙山寺、湄洲开基妈祖的天后宫、还有彰化市孔子庙等，保存了相当多古朴建筑极具地方。?</p>', '0', '2015-05-12 16:16:02', '', '2017-07-19 14:02:16', '', '0', 'group1/M01/46/4A/eBif6Flu8yOAH7r3AAES1lfJdq8043.jpg', 'group1/M01/46/4A/eBif6Flu9XSAbGzIAAOA4xBotI0724.jpg,group1/M01/46/4A/eBif6Flu9XaAGq_QAADaS4-i1Xc813.jpg,group1/M01/46/4A/eBif6Flu9XiAEW9SAAHrm9XAAXY708.jpg,group1/M01/46/4A/eBif6Flu9XyAW7PgAASNWYjf-Ek886.jpg,group1/M01/46/4A/eBif6Flu9X6AeCRDAAPfi0-0tFY036.jpg,group1/M01/46/4A/eBif6Flu9YKAbjL8AAFMwCfDU5I451.jpg,group1/M01/46/4A/eBif6Flu9YaADVirAAbKf4SqG2o552.jpg');
INSERT INTO `sys_city` VALUES ('711900', '240', '710000', '嘉义', 'JY', 'J', '120.461392', '23.487846', '', '', '0', '0', '0', '8', '0', '0', '1', '<section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">简介</span></p></section><p style=\"text-align: justify; color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">嘉义位于台湾省中部，是一座美丽又充满魅力的小城。嘉义交通便利，背山面海，风景秀丽，四季如春，景物天成。这样的嘉义市自然是著名的观光之地了，景点有很多，比如佛教圣地弥陀禅寺，九华山地藏庵等。而且还经常会有一些具有当地特色的节日活动，更有非常多的各种特色夜市，对游人的吸引了就更大了。</span>?</p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(248, 122, 122); box-sizing: border-box;\">&nbsp;</section><section style=\"display: inline-block; padding: 5px; box-sizing: border-box;\"><p><span style=\"font-size: 18px; color: #F87A7A;\">最佳旅行时间 </span></p></section><p style=\"color: rgb(127, 127, 127); font-family: 微软雅黑; font-size: 14px; text-indent: 2em; margin-top: 5px; margin-bottom: 5px; line-height: 1.5em;\"><span style=\"color: rgb(102, 102, 102); font-family: Arial, &quot;Lucida Grande&quot;, &quot;Microsoft Yahei&quot;, &quot;Hiragino Sans GB&quot;, &quot;Hiragino Sans GB W3&quot;, SimSun, &quot;PingFang SC&quot;, STHeiti;\">9月至11月</span></p><section class=\"assistant\" style=\"margin: 5px; border-radius: 5px; display: inline-block; line-height: 28px; color: rgb(255, 255, 255); background-color: rgb(